INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ALI', '271', 'Language Tutorial for International Teaching Assistants', '', 'Individualized tutorial on the language and oral skills used by International Teaching Assistants in the performance of his or her duties. Based on observation and feedback. Open to International Teaching Assistants only. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ALI', '274', 'Advanced Academic and Professional Spoken English', '', 'Required for International Teaching Assistants (ITAs) who, after taking the ITA Oral Interview Exam, are determined to need the particular skills taught in this course.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ALI', '275', 'Writing for Publication and Dissertations', 'James Polk', 918308, 'Elective course for international graduate students focusing on conventions of advanced academic writing and problems in syntax, vocabulary, and register for writing and/or publishing dissertations. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '101mgw', 'Race and Class in Los Angeles', 'Alicia Chavez', 1859228, 'Analysis of race and the economic, political, gender, and social dimensions of contemporary Los Angeles including topics such as residential segregation, economic inequality, and city politics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '135mgw', 'Peoples and Cultures of the Americas', 'Alicia Chavez', 1859228, 'An introduction to cultures and people in the Americas; the social, historical, economic and cultural formations that together make up the Latino/a American imaginary.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '140gw', 'Borderlands in a Global Context', '', 'Interdisciplinary survey of theory and borderland site cases, national sentiment, linguistic and cultural conflicts, exploration of local, regional, and national identities in cultural contact zones.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '150gw', 'The American War in Viet Nam', 'Viet Nguyen', 1898212, 'Examination of the ways that different nations and cultures have remembered the Vietnam War or the American War as it is called in Vietnam. (Duplicates credit in former AMST 377)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '200mg', 'Introduction to American Studies and Ethnicity', 'Alicia Chavez', 1859228, 'Introduction to American studies and ethnic studies. Provides an overview of major theories, concepts, and issues.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '204g', 'Introduction to Native Studies', 'Chris Finley', 'Overview of Native American studies, including Indigenous intellectualism and resistance through language revitalization, film-making, and the recognition of one another.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '205g', 'Introduction to American Popular Culture', '', 'Examines the relation between U.S. national culture, race, and popular culture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '206mg', 'The Politics and Culture of the 1960s', 'Rick Berg', 'Examines political and cultural change in the United States during the decade of the 1960s.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '220mgw', 'The Making of Asian America', 'Viola Lasmana', 'Historical, social, and cultural analysis of (East, South, and Southeast) Asians in the United States. Theme examined: immigration, race and gender relations, ethnic culture, community, and identity.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '250mgw', 'The African Diaspora', 'Edwin Hill', 2067531, 'History, political-economy and aesthetics of the African Diaspora with emphasis on Latin America, the Caribbean, Europe and Africa.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '285mg', 'African American Popular Culture', 'Lanita Jacobs', 'Examines history of popular cultural forms such as literature, music, dance, theater, and visual arts produced by and about African Americans.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '301gp', 'America, the Frontier, and the New West', 'Thomas Gustafson', 315213, 'Introduction to an interdisciplinary study of American political, cultural and social life with a particular emphasis on the Western United States as a region. Recommended preparation: HIST 200, ENGL 263', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '332m', 'Post-Civil Rights Black America', 'Kimberly McNair', 2308569, 'Analysis of the political, economic, and cultural experiences of the post-1965 period through an interrogation of contemporary conditions, movements, and responses to power in Black America.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '340m', 'Latina/o LA', 'Natalia Molina', 'Examination of spatial and social patterns of the Latino population in Los Angeles. Emphasis on economic, demographic and cultural processes. Duplicates credit in the former GEOG-340.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '350', 'Theories and Methods', 'Alicia Chavez', 1859228, 'Advanced study in interdisciplinary theories and methods for analyzing race and ethnicity in the United Stes, including a comparative study of topics such as inequality, gender, and class.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '365', 'Leadership in the Community - Internship', 'Stanley Huey', 1513381, '8-10 hours per week in a community-based internship plus 2 hour class discussion. Theoretical and practical issues associated with community leadership.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '373m', 'History of the Mexican American', '', 'Racial and cultural background of Mexico; immigration and conquest; the Mexican in California and the southwest; the rise of contemporary Mexican-American consciousness.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '392', 'Undergraduate Research Methods', 'Carlos Antonio Delgado', 'Examination of processes of scholarly research; quantitative and qualitative research methods; faculty mentorship; experiential learning; research proposal writing; careers in research. Sophomore or junior standing in the major. Departmental approval.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '440', 'Reading American Culture Through Graphic Novels', 'Dana Johnson', 1871225, 'Examine American identity and culture through exploration of graphic novels that vary widely in theme, including ancestry, race, gender, sexuality, throughout American history.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '448m', 'Chicano and Latino Literature', 'Elda Maria Roman', 'Development of the poetry, essay, short story and novel of the Chicano and Latino peoples of the United States, with particular emphasis on the differentiating characteristics between the multiple cultures that constitute the Latino populations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '452m', 'Race, Gender and Sexuality', 'Chris Finley', 'Examination of sexual discourses in the United States in the context of slavery, empire, sex work, labor markets, schools and prisons.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '498', 'Senior Seminar in American Studies and Ethnicity', 'David Roman', 474784, 'Capstone course for majors, highlighting interdisciplinary study of race and ethnicity in a comparative context.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '510', 'Readings in Chicano/Latino Studies', 'Natalia Molina', 'Perspectives from the major debates that have driven the development of the field of Chicano/Latino/a Studies across the disciplines.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '530', 'Readings in African American Studies', 'Francille Wilson', 1481177, 'Seminar exploring crucial theoretical, methodological and historical issues in the development of African American Studies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '552', 'Archives and Subcultures', 'Nayan Shah', 1822876, 'Introduction to the practice of archival research with an emphasis on the literary and historical methods of documenting subcultural groups, particularly racial and sexual minorities.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '570', 'Readings on Los Angeles and Urban Culture', 'Juan De Lara', 1637633, 'Exploration of some of the leading scholarship from a variety of disciplines writing about Los Angeles and the southern California area. Particular emphasis is placed on the intersections of historical, contemporary and cultural issues that inform recent scholarship on Los Angeles.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '660', 'Interdisciplinary Research Seminar in Race and Ethnicity', 'Sarah Gualtieri', 1097474, 'Explores issues of conducting interdisciplinary research in race and ethnicity and guides students through the design and completion of a journal-quality research paper. Recommended preparation: graduate reading course in race and ethnicity', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AMST', '701', 'Contemporary Theories of American Studies and Ethnicity', '', 'Seminar in representative theoretical works in the fields of American Studies and Ethnic Studies published in the past fifteen years.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '101g', 'Introduction to Medical Anthropology', 'Erin Moore', 677848, 'The body, illness and healing from a cultural perspective, including comparative studies of folk healing systems, curing rituals and Western biomedical practices.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '202g', 'Our Human Past', 'Kenneth Seligson', 2304187, 'Archaeology as the means of investigating our shared human past, from the origins of humanity to the foundations of current civilization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ANTH', '263g', 'Exploring Culture Through Film', 'Xochitl Ruiz', 'Concepts of social anthropology using filmic representations ofsocieties throughout the world in contrast to written ethnography.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '305', 'Childhood, Birth and Reproduction', 'Erin Moore', 677848, 'Cross-cultural analysis and comparison of the experience and cultural conception of birth, maternity, parenthood, and childhood in western and non-western societies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '315g', 'North American Indians', 'Tok Thompson', 1207495, 'North American Indian societies, their major cultural themes,ethnological significance, and comparability with Western Europeancultural forms; lectures, visual, and indigene demonstrations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ANTH', '325', 'Global Studies Research Methods', 'Xochitl Ruiz', 'Methods for field research in international settings include ethnography, archival work, surveying and documentation; preparation for overseas research and senior thesis. Recommended preparation: ANTH-205.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '333mg', 'Forms of Folklore', 'Tok Thompson', 1207495, 'Introduction to folklore as a discipline, including folklore research methods and theory. Core course for the minor in Folklore and Popular Culture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '338g', 'The Anthropology of What We Eat and Why', 'Emily Zeamer', 2083371, 'An introduction to the field of food studies, as a historical and cross-disciplinary study of the place of food in culture and society.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '371mg', 'Cross-Cultural Research on Urban Gangs', 'Thomas Ward', 1514243, 'Youth gang dynamics and their effects on institutions. Comparative analysis of Asian, African, and Mexican American gangs.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '373', 'Magic, Witchcraft and Healing', 'Thomas Ward', 1514243, 'Analysis of the practices of witches and witch doctors, priests, diviners and traditional healers in Western and non-Western societies, relating their practices to religion and medicine.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '375', 'Human-Centered Research Practicum', 'Emily Zeamer', 2083371, 'Practical training in qualitative research methods for design consulting and real-world problem solving in development, government, and corporate contexts. Socially-engaged ethnography; anthropology as cultural critique.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '393', 'Directed Internship', 'Emily Zeamer', 2083371, 'Real-world work experience in an internship position, while also learning to apply anthropological theory and research methods to complete a related research project. Enrollment requires permission from department.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ANTH', '410b', 'Ethnographic Field Methods and Practicum', 'Lanita Jacobs', 'Survey of anthropological methods for acquiring and analyzing data. Implementation of the field project.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ANTH', '472', 'Stills', '', 'Visual techniques for data collection and analysis in anthropological research. Visual anthropology research using 35 mm. photography skills, fieldwork procedures, data analysis, and presentation formats.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '485', 'Global Studies Senior Thesis Seminar', 'Janet Hoskins', 315194, 'Provides senior Global Studies majors with the practical and theoretical skills needed to write a successful undergraduate thesis in Global Studies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '502', 'Contemporary Theory in Anthropology', 'Cheryl Mattingly', 600147, 'Continuation of 501, focusing on current models, methods and issues in social anthropology.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '577L', 'Advanced Anthropological Media Seminar', 'Jennifer Cool', 2182159, 'A hands-on laboratory based survey of post-production technologies, including editing both new and older footage. Students should be finishing their own documentaries.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '602', 'The Anthropology of Popular Culture', 'Tok Thompson', 1207495, 'The relationship between anthropology and popular culture is explored through a critical examination of the category "popular culture."', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARAB', '122', 'Arabic I', '', 'Introduction to Modern Standard Arabic: Development of speaking, listening, reading, and writing skills in contemporary cultural contexts. (Duplicates credit in former MDES 122)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARAB', '152', 'Arabic II', '', 'Continuation of Arabic I. Prerequisite: ARAB-122. (Duplicates credit in former MDES 152)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ARAB', '222', 'Arabic III', 'Sozan Wali', 2155131, 'Intermediate Modern Standard Arabic. Continued development of speaking, listening, reading, and writing skills. Prerequisite: ARAB 152. (Duplicates credit in former MDES 222)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARAB', '252', 'Arabic IV', '', 'Continuation of Arabic III. Continued development of speaking, listening, reading, and writing skills in Modern Standard Arabic. Prerequisite: ARAB 222. (Duplicates credit in former MDES 252)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ARAB', '300', 'Introduction to Translation', 'Sozan Wali', 2155131, 'Introduces students to the fundamentals of Arabic-English translation and offers analytical, linguistic and cross-cultural factors affecting interpreting and translation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ARAB', '352', 'Advanced Arabic II', 'Sozan Wali', 2155131, 'Continuation of Advanced Arabic I. Prerequisite: ARAB 322. (Duplicates credit in former MDES 352)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '100g', 'Introduction to Visual Culture', 'Megan Luke', 1782972, 'The description and analysis of various forms of visual culture, including both mass media and "high" art representations, both Western and non-Western images.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '121gp', 'Renaissance to Modern', 'Amy Ogata', 2025331, 'European art and its legacy in the Americas. Painting, sculpture, architecture and other visual media considered in relation to social and cultural history.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '128gp', 'Arts of Latin America', '', 'Survey of the art, architecture, and visual culture of Latin America from the colonial period to the present, focusing on connections to culture and society.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '201g', 'Digging into the Past', 'John Pollini', 947757, 'A broad survey, covering some 8,000 years and focusing on the material culture of the ancient world in a historical and social context.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '220g', 'Medieval Visual Culture', 'Carolyn Malone', 947756, 'Medieval visual culture as an introduction to the Christian heritage of western civilization and to the interaction of Church and state from the 3rd to the 13th century.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '230', 'Art and Culture in Early Modern Europe', 'Aaron Wile', 'Survey of European art from the 15th to the 17th century. Case studies in Renaissance and Baroque art with emphasis on artists in major urban centers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '250mg', 'Art, Modernity and Difference', 'Megan Luke', 1782972, 'Consideration of various categories of "The Modern" as they have been constructed in Western art of the late 19th and 20th centuries.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '322', 'Roman Art and Archaeology', 'John Pollini', 947757, 'Survey of the art and architecture of ancient Rome and its empire, from the beginnings of the city through Constantine (8th century BCE to 4th century CE).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '344', 'Baroque Art', 'Susanna Berger', 'Painting, sculpture and architecture in 17th century Europe, north and south. Recommended preparation: AHIS 121', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '369', '1851-1940', 'Amy Ogata', 2025331, 'An examination of European modern art and design, focusing on industrialization, urbanism, primitivisim, colonialism, and their relations to the arts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '370g', '1940 to the Present', 'Samantha Burton', 2210643, 'Questions of social engagement and political structure address this examination of major international movements in art since 1940.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '387', 'Later Japanese Art', 'Rika Hiro', 'A survey of Japanese architecture, garden design, ceramics, and painting from 1333 to the present, stressing the role of art within cultural context.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '460', 'Studies in 18th and 19th Century Art', 'Hector Reyes', 1983303, 'In-depth exploration of specified topics within the area of 18th and 19th century art and architecture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '465', 'Studies in American Art', '', 'In-depth exploration of a specified topic in the history of American art.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '484', 'Studies in Chinese Art', 'Sonya Lee', 585103, 'In-depth exploration of specified topics within the area of Chinese art and architecture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '494', 'Undergraduate Proseminar in Art History', 'Samantha Burton', 2210643, 'Historiography and methodology: introduction to techniques of research and writing. Required of all art history majors, preferably in junior year.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '499', 'Special Topics', 'Selma Holo', 'Comprehensive exploration of particular aspects of the history of art.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '512', 'Seminar in Renaissance Art', 'Susanna Berger', '', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '516', 'Seminar in the History of Photography and Visual Culture', 'Vanessa Schwartz', 1236490, 'Explores the history of photography with emphasis on the way the medium has been used as a form of communication and knowledge transmission.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '100Lxg', 'The Universe', 'Vahe Peroomian', 2002099, 'Survey of the universe: planets, satellites, comets, stars, nebulae, galaxies. Practical component includes planetary observations and dark-sky field trip. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '200Lg', 'Life in the Universe', 'Vahe Peroomian', 2002099, 'Survey of the prospects for life in our solar system and beyond, based on the evolution of life on Earth.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '422', 'Galaxies and Large-Scale Structures in the Universe', 'Elena Pierpaoli', 2040068, 'Galaxies and clusters of galaxies: their content, structure, dynamics, distribution, and motions; the cosmic microwave background: theory and observation; elements of observational cosmology. Prerequisite: PHYS-153L or PHYS-163L.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '102Lxg', 'Humans and Their Environment', 'Dale Kiefer,James Moffett', 'An examination of the physical and biological laws that influence agriculture, pollution, population dynamics (including humans), climate, biodiversity and ecosystem structure and function. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '103Lxg', 'General Biology for the Environment and Life', 'John Heidelberg,Trond Sigurdsen', 'Study of common skills in biology, including basics of evolution, systematics, ecology, genetics, biochemistry and molecular biology, physiology, and anatomy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '104Lxg', 'How the Body Works', 'Chien-Ping Ko,Shirin Birjandi', 'Structure and function of the human body, including the role of organ systems, tissues, and cells in normal function. Malfunctions resulting from disease, substance abuse and lifestyle. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '110g', 'Good Genes, Bad Genes', 'Matthew Michael', 1723731, 'Introduction to the scientific method; basic instruction in molecular biology with emphasis on how biological research advancement has impacted medicine, commerce and society in general.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '115Lxg', 'The Biology of Food', 'Grayson Jaggers', 2252849, 'Exploration of molecular biology, biochemistry, microbiology, nutrition and the history of biology through the study of food, focusing on food\'s relationship to the biological world.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '120Lg', 'Organismal Biology and Evolution', 'Raffaella Ghittoni', 1969518, 'In-depth survey of key topics related to advances in our knowledge of the diversity of life and evolution; origin of life; eukaryotes/prokaryotes; ecology.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '150Lxg', 'The Nature of Human Health and Disease', 'Raffaella Ghittoni,Nancy Castro', 'The human organism; the nature of inherited and acquired diseases;the biological and societal basis for the AIDS epidemic; therapy,drug design and the future. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '180Lxg', 'Evolution', 'Oliver Rizk', 'Changes in the physical and biological universe over time; origins of life, dinosaurs, human evolution. Implications of evolutionary mechanisms and mass extinctions for human survival.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '194', 'Introduction to Research II', 'Albert Herrera', 960918, 'A series of lectures and discussions at which faculty of the department introduce their research activities to students entering biology and related majors.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '199', 'Neuroscience Colloquium', 'Jason Zevin', 2062302, 'Introduction to research activities conducted by Neuroscience faculty at USC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '220Lg', 'Cell Biology and Physiology', 'Albert Herrera,Alan Watts', 'In-depth survey of key topics related to advances in our knowledge of cellular biology and physiology; cell composition/metabolism; gene action; organism structure and function. Recommended preparation: high school chemistry; BISC 120L or BISC 121L.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '221Lg', 'Cell Biology and Physiology', 'David McKemy,Bruce Yazejian', 'Equivalent to 220L, but taught at a higher level for exceptionally well-prepared students. Admission to the course by departmental approval only.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '230Lxg', 'The Biology of the Brain', 'Bruce Yazejian', 1939387, 'The structure and function of the mammalian brain including the role of the brain in regulating behavior, both in normal and diseased states; in relation to mind; and in comparison with machine forms of intelligence.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '305', 'Introduction to Statistics for Biologists', 'Peter Calabrese,Liang Chen', 'Statistical methods in biological science and medicine, including populations and samples, random sampling, confidence intervals, paired samples and regression.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '307L', 'General Physiology', 'Albert Herrera', 960918, 'Physiological functions of the circulatory, digestive, endocrine, integuementary, musculoskeletal, nervous, respiratory, and urogenital systems in animals. Lecture, 3 hours; Laboratory, 3 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '312x', 'Molecular Biochemistry', 'Christa Bancroft,Nancy Castro', 'Structure and function of biological macromolecules; major metabolic pathways including glycolysis and photosynthesis. DNA replication, repair, and recombination; gene expression, regulation and epigenetics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '313L', 'Evolution and Population Genetics', 'Oliver Rizk,Sergey Nuzhdin', 'History of evolutionary thought; molecular basis for evolution; dynamics of genes in populations; specialization and macroevolution; patterns of evolution. Recommended preparation: BISC-320, BISC-325, and familiarity with algebra, basic chemistry, and basic physics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '321', 'Science, Technology and Society', 'Peter Calabrese', 'Builds upon a basic science background to provide students with an awareness of cutting edge scientific research, its technological applications and its societal ramifications.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '330L', 'Biochemistry', 'Fabien Pinaud,Grayson Jaggers,Nancy Castro', 'Basic biochemical principles; classes of molecules - structure and function; cellular energetics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '406L', 'Biotechnology', 'Christa Bancroft', 2014331, 'Techniques in molecular biology and biochemistry applied to prokaryotic and eukaryotic model systems; applications of recombinant DNA and genomic technology. Recommended preparation: BISC 313 or BISC 325', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '407', 'Cellular and Molecular Neuroscience', 'Dion Dickman,Bruce Herring', 'An examination of individual nerve cell development, target location, and establishment of functional synapses; how dysfunction in these processes contributes to neurological and neuropsychiatric diseases.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '408', 'From Synapses to Perception', 'Sarah Bottjer,Bruce Yazejian', 'Sensory and motor systems to illustrate concepts regarding the functional organization of the brain, from microcircuitry of neural circuits to global processes such as perception. Prerequisite: BISC 421.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '410', 'Applications of Molecular Biology to Medicine', 'Peter Kuhn', 'Advances and trends in the understanding, diagnosis, and treatment of human diseases. A mixture of lectures, seminars and resulting discussions by the students. Senior standing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '411', 'Advanced Cell Biology', 'Emily Liman,Donald Arnold', 'The synthesis, transport and assembly of the complex structures that mediate eukaryotic cellular function. Electrical and biochemical mechanisms underlying intercellular communication. Prerequisite: BISC-220 or BISC-221 and BISC-320.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '414', 'Biology of Cancer', 'Susan L Forsburg,John Tower', 'Focus on the advances in molecular biology of cancer, from fundamental molecular signaling pathways to DNA repair to stem cell biology, through primary research literature reviews', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '419L', 'Environmental Microbiology', 'Eric Webb,Jed Fuhrman', 'Qualitative and quantitative appraisal of microbial activities in pure and contaminated environments; microbial community and its development; interspecific relationships; effects of microorganisms on their surroundings. Prerequisite: BISC 330L. Recommended preparation: BISC 300L.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '423', 'Biological Basis of Neurological Disord', 'Michael Quick', 683003, 'Examination of various neurological disorders originating from developmental signaling and/or anatomical abnormalities.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '424', 'Brain Architecture', 'Larry Swanson', 'How the parts of the brain are interconnected to form a complex biological computer, from historical, evolutionary, and developmental perspectives.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '427', 'The Global Environment', 'Sergio Sanudo-Wilhelmy', 'Earth\'s development as a habitable planet, from origin to human impacts on global biogeochemical cycles in the ocean, land, atmosphere. Discussion of environmental alternatives.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '435', 'Advanced Biochemistry', 'Xiaojiang Chen,Myron Goodman,John Petruska', 'Macromolecular structure and function; enzymology; metabolic regulation. Lecture, 3 hours; discussion, 2 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '469L', 'Marine Biology', 'Karla Heidelberg,Carly Kenkel', 'Oceanography and marine biology, sampling techniques, evolutionary adaptations, morphology, systematics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '478', 'Computational Genome Analysis', 'Andrew Smith,Mark Chaisson', 'Introduction to and applications of algorithms and statistics to genome analysis. Analysis of physical and genetic maps, DNA sequencing, sequence comparisons, DNA chips.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '480', 'Developmental Biology', 'Sergey Nuzhdin,Rusty Lansford', 'Basic mechanisms of animal development are considered at different levels of analysis. Emphasis is on molecular, genetic, and cellular processes underlying vertebrate and invertebrate development. General concepts and evolutionary mechanisms are emphasized. Lecture, 3 hours; discussion, 2 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '483', 'Geobiology and Astrobiology', 'Douglas Capone,Jan Amend', 'Relationships between microbiota and the earth environment including the hydrosphere, lithosphere and atmosphere, with consideration of the potential for life on other planets.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '485', 'Advanced Seminar in Bacterial Survival and Evolution', 'Steven Finkel,Kenneth Nealson', 'Literature-based seminar in current topics in microbial evolution and adaptation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '493x', 'Honors Seminar', 'John Petruska,Cornelius W Sullivan', 'Not available for graduate credit.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '494x', 'Honors Thesis', 'John Petruska,Cornelius W Sullivan', 'Not available for graduate credit.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '499', 'Special Topics', 'Raymond Stevens', 'Lecture and discussion in specialized areas of the biological sciences. Students cannot register more than twice for this course. Not open to freshmen and sophomores. Recommended preparation: BISC 220L or BISC 221L, BISC 320L, BISC 325 and BISC 330L.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '502b', 'Molecular Genetics and Biochemistry', 'Xiaojiang Chen,John Tower,Sergey Nuzhdin,Fabien Pinaud,Sean Curran', 'Current genetic and biochemical analysis of replication, recombination, mutagenesis, and repair. Fundamentals of transcription and regulation of gene expression. Recent applications of genetic engineering and genome analysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '529', 'Seminar in Marine Biology', 'Suzanne Edmands', 642836, '', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '542', 'Seminar in Molecular Biology', 'Andrew Smith', '', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '544', 'Advanced Reading in Molecular Biology', 'Susan L Forsburg', 'Advanced training for molecular biology graduate students in reading primary journal articles. Emphasis on critical analyses of primary scientific literature.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '549', 'Seminar in Integrative and Evolutionary Biology', 'Jill L McNitt-Gray,Lindsey Schier', 'Current topics in integrative and evolutionary biology.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '577a', 'Computational Molecular Biology Laboratory', 'Fengzhu Sun', 2209771, 'Practical experience in computational molecular biology applications. Mathematical and statistical software packages relevant for genomic analysis. Retrieval and analysis of genomic data from databases. Recommended preparation: higher level programming language.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '583', 'Evolution and Adaptation of Marine Organisms', 'Suzanne Edmands,Andrew Gracey,Eric Webb', 'Fundamentals of evolutionary patterns and processes in the marine environment, with emphasis on rates of adaptation to a changing ocean', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '593', 'Practicum in Teaching the Biological Sciences', 'Peter Calabrese', 'Practical principles for the long-term development of effective teaching within College disciplines. Intended for teaching assistants in Dornsife College.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '050x', 'General Chemistry Tutorial', 'Surya Prakash', 'Weekly tutorial for selected students in CHEM 105aL. Strong emphasis on chemical mathematics and key concepts in general chemistry. Topics parallel lectures in CHEM 105aL. Not available for degree credit. Graded CR/NC. Discussion, 2 hours.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '051x', 'General Chemistry Tutorial', 'Jessica Parr', 1041016, 'Structured tutorial for selected students in CHEM 105bL. Strong emphasis on chemical mathematics and key concepts in general chemistry. Topics parallel lectures in CHEM 105bL.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '105aLg', 'General Chemistry', 'Thomas Bertolini', 1436158, 'Fundamental principles and laws of chemistry; laboratory work emphasizes quantitative procedures. Prerequisite to all more advanced courses in chemistry. Lecture, 3 hours; laboratory and discussion, 4 hours. Quiz, 1 hour.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '105bL', 'General Chemistry', 'Rebecca Broyer,Jasmine Bryant', 'Fundamental principles and laws of chemistry; laboratory work emphasizes quantitative procedures. Prerequisite to all more advanced courses in chemistry. Lecture, 3 hours; laboratory and discussion, 4 hours. Quiz, 1 hour. Prerequisite: one year of high school chemistry; CHEM-105aLg or 115aLg.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '108L', 'Kinetics and Equilibrium', 'Chiho Mak', 'Fundamental principles and laws of chemistry emphasizing contemporary chemistry; laboratory work focuses on quantitative procedures. Second-semester general chemistry course for majors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '115bL', 'Advanced General Chemistry', 'Jessica Parr,Jasmine Bryant', 'Equivalent to 105agL-bL, but taught at a higher level for exceptionally well-prepared students. Admission to course by departmental approval only. Lecture, 3 hours; lab and discussion, 4 hours; quiz, 1 hour. Prereq: 1 year high school chemistry and mathematics through pre-calculus.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '205Lxg', 'The Science, and Its Impact', 'Thomas Bertolini,Rebecca Broyer,Jasmine Bryant', 'Scientific principles underlying forensic approaches to the investigation of crimes and its societal impact on law, culture and media. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '251x', 'Organic Chemistry Tutorial B', 'Rebecca Broyer', 1538668, 'Structured tutorial course, which enables students to understand fundamental principles, better approach problems with success. Explores the major principles of organic chemistry and its relevance. Topics covered will parallel 322B.Prerequisite: CHEM 322aL. Corequisite: CHEM-322bL. Graded CR/NC. Not available for degree credit.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '300L', 'Analytical Chemistry', 'Sri Narayan,Mark Thompson', 'Theory and practice in chemical analysis, emphasizing instrumental techniques; error analysis, fractional distillation, extraction; chromatograhpy; visible, ultraviolet, and infrared spectroscopy; introductions to electrochemistry and nuclear magnetic resonance spectroscopy. Lecture, 2 hours; Laboratory and Discussion.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '322aL', 'Organic Chemistry', 'Thomas Bertolini', 1436158, 'Chemistry of the carbon compounds of the aliphatic and aromatic series; laboratory preparation of typical compounds of both series. Lecture, jointly with 325aL and 325bL, 3 hours; laboratory and discussion, 4 hours. For premedical and predental students, and some categories of biology majors and engineers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '322bL', 'Organic Chemistry', 'Rebecca Broyer,Travis Williams', 'Chemistry of the carbon compounds of the aliphatic and aromatic series; laboratory preparation of typical compounds of both series. Lecture, 3 hours jointly with CHEM 325abL; laboratory and discussion, 4 hours. For premedical and predental students, and some categories of biology majors and engineers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '325bL', 'Organic Chemistry', 'Valery Fokin', 2107041, 'Required of majors in chemistry. Lecture, 3 hours a week with 322abL; laboratory and discussion, 7 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '332L', 'Physical Chemical Measurements', 'Susumu Takahashi', 'Experimental study of topics discussed in CHEM 430 and CHEM 431; adsorption, magnetic susceptibility; electron spin resonance, kinetics, equilibria, molecular spectra and structure, viscosity, dielectric properties.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '423L', 'Advanced Laboratory Techniques in Organic and Inorganic Chemistry', 'Nicos Petasis', 'Advanced synthetic, analytical, and physical measurement techniques in organic and inorganic chemistry. Emphasis on laboratory work with discussion of theoretical background. Lecture, 2 hours; discussion, 1 hour; laboratory, 8 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '431', 'Quantum Mechanics', 'Vadim Cherezov', 'Kinetic theory; equations of state; thermodynamics; phase equilibria; chemical equilibrium; nuclear chemistry, wave mechanics; spectroscopy; statistical thermodynamics; kinetics; electrochemistry; surface and colloid chemistry.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '432', 'Physical Chemistry for the Life Sciences', 'Lin Chen', 2284848, 'Principles of physical chemistry relevant for the life sciences: thermodynamics, chemical equilibria, molecular dynamics, kinetics, molecular structures and interactions, spectroscopy, statistical thermodynamics and macromolecular structures. Duplicates credit in CHEM 430a.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '453', 'Advanced Inorganic Chemistry', 'Smaranda Marinescu', 'Atomic structure, theory of bonding, molecular structure, metallic state, coordination compounds, transition and nontransition metals, magnetic and optical properties, crystal field theory, mechanism of reactions.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '455', 'Chemical Nanotechnology', 'Richard Brutchey', 1433831, 'Studies in the fundamental principles governing nanoscale materials. Structure and chemical bonding, preparative methods, and electrical, optical and magnetic properties of nanoscale materials and applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '463', 'Chemical Nanotechnology Laboratory', '', 'Experimental techniques in the synthesis and characterization of nanoscale materials. Emphasis on examining size-dependent properties of various nano - scale materials using spectroscopic techniques.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '516', 'Synthesis, Reactivity and Mechanism in Inorganic and Organometallic Chemistry', 'Megan Fieser', 'A reactivity framework for inorganic and organometallic chemistry; synthesis, reaction mechanisms, and energetics; homogeneous catalysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '520b', 'Advanced Chemical Biology', 'Matthew Pratt', 1314253, 'Chemical approaches to investigate protein function and posttranslational modifications. Recommended preparation: CHEM 322ab, and BISC 330 (or equivalent).', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '527', 'Synthetic Organic Chemistry', 'Kyung Jung', 1379308, 'A survey of representative groups of widely used synthetic organic reactions; emphasis on scope, limitations, and stereochemical consequences.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '535', 'Introduction to Molecular Spectroscopy', 'Alexander Benderskii', 2343371, 'Theoretical and experimental methods of molecular spectroscopy and applications to chemistry. Rotational, vibrational, electronic, electron and nuclear magnetic resonance spectroscopies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '540', 'Introduction to Statistical Mechanics', 'Oleg Prezhdo', 2077514, 'Study of macroscopic systems from molecular viewpoint using statistical mechanics: ensembles, fluctuations, gases, gas-solid interfaces, crystals, polymers, critical phenomena, non-equilibrium systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '561', 'Polymer Synthesis', 'Barry Thompson', 1923008, 'Concepts of polymer Structure and sterochemistry. Organic chemistry of polymerization reactions with emphasis on condensation, radical, cationic, anionic, and coordination-metathesis polymerization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '575', 'Modern Trends in Physical Chemistry', 'Alexander Benderskii', 2343371, 'Emerging research areas in physical and theoretical chemistry through a thorough discussion of seminal research articles and presentations of current research topics. Recommended preparation: some research experience and familiarity with literature search.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '577a', 'Medicinal Chemistry and Drug Design', 'Charles Mc Kenna', 'a) Introduction to fundamentals of medicinal chemistry with special attention to the drug discovery process. Recommended Preparation PSCI 664 and CHEM 519.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '588', 'Advanced Practical X-ray Structure Determination of Small Mo', 'Ralf Haiges', 'Advanced techniques and methods in small molecule X-ray crystal structure determination. Emphasis on practical and laboratory work.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '589', 'Macromolecular Crystallography', 'Vadim Cherezov', 'Advanced techniques and methods in X-ray crystal structure determination of biological macromolecules with emphasis on practical work.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '625', 'Chemical Applications of Magnetic Resonance Spectroscopy', 'Ralf Haiges', 'Elementary theory of magnetic resonance spectroscopy, methods of spectral analysis, treatment of Fourier transform methods and time dependent phenomena; recent applications in organic chemistry.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '151gp', 'The Legacy of Rome', 'Anthony Boyle', 1210125, 'Ancient Roman values, ideas, and institutions of relevance to later periods of civilization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '160gp', 'Ancient Lives', 'Vincent Farenga', 665017, 'The lives of prominent Greeks, Romans and others in their ancient historical and biographical contexts and in modern reevaluations through various written and visual media.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CLAS', '170gp', 'from Ancient Greece Through Contemporary LA', 'Jeffrey Kahane,Thomas Habinek', 'Joint introduction to classical music and classical literature through close study of works of music inspired by the Greek and Roman literary tradition.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '280gp', 'Classical Mythology in Art and Literature', 'Thomas Habinek', 34529, 'Classical Greek and Roman mythology as inspiration for literature, art, music, and film in various historical contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '349g', 'Ancient Empires', 'Christelle Fischer-Bovet', 1779824, 'History and cultures of the ancient empires of southwest Asia, from Cyrus the Great to the establishment of Islam.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '380', 'Approaches to Myth', 'Stefano Rebeggiani', 2126304, 'Advanced study of uses and interpretations of myth. Approaches include myth and ritual; psychology; gender; myth in literature, film and art. Recommended preparation: CLAS 280', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CLAS', '420', 'Science and Empire from Baghdad to Byzantium', 'Alexandre Roberts', 'Seminar on medieval science and scientists in the Byzantine and Islamic empires, including astronomy, astrology, medicine, and alchemy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '465', 'Archaeology and Society', 'Lynn Dodd', 1422758, 'The interaction of archaeology and contemporary societies through political and moral claims; archaeologists\' role as stewards and interpreters of ancient cultures and their remains. Capstone course for the Archaeology major.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CLAS', '520', 'Approaches to Antiquity', 'Alexandre Roberts', 'Study in the history and theory of classical scholarship.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '550', 'Seminar in Classical and Hellenistic Literature', 'William Thalmann', 315204, 'Tragic poetry, comic poetry, Hellenistic poetry.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '575', 'Seminar in Roman History, Culture, and Society', 'Christelle Fischer-Bovet', 1779824, 'Introduction to research in Roman history and historiography.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '102g', 'The Place of Literature in Global Cultures', 'Michael Du Plessis', 1307922, 'Comparative study of works from a broad range of cultural traditions that originate from, and provide insight into, vital global locations outside the Western sphere.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '251g', 'Modern Literature and Thought of the West Since 1800', 'Roberto Diaz', 1168906, 'Survey of literary and other cultural texts from the 19th to the 21st centuries, with emphasis on the individual and social change.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '303', 'Culture, Change, Resistance', 'Neetu Khanna', 1829094, 'Cultural dimensions of issues in globalization: migration, diaspora, terrorism, communications, climate change, collectives, production and technology, money and exchange.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '374mg', 'Women Writers in Europe and America', 'Margaret Rosenthal', 1207521, 'Introduction to works of major women writers from the Middle Agesto the 20th century in their literary, social, and cultural contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COLT', '437', 'Writing and Interpreting the Self', 'Veli N Yashin', 'Exploration of twentieth-century Arabic autobiographical writings and interrogation of the complex ways by which such works unsettle fundamental assumptions of literary history and modernity.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '475', 'Politics and the Novel', 'Neetu Khanna', 1829094, 'Examination of the modern realist novel with special focus on the representation of social change (revolution, class conflict, sexual politics).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COLT', '511', 'Translating Race', 'Olivia C Harrison', 'Contemporary notions of racial, cultural, and religious difference across languages and history.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COLT', '525', 'Studies in Literary and Cultural History', 'Veli N Yashin', 'Literary and cultural currents from classical antiquity through modernity. Varying focus on specific genres, periods, movements, or problematics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '101g', 'Thematic Option Honors Progr', 'David Treuer', 2176725, 'Study of the structures through which we shape our experience in religion, philosophy, literature, music, and the visual arts, and of competing theories of interpretation. Students may not take this course on a P/NP basis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '103g', 'Thematic Option Honors Pro', 'Matthew Kahn', 2133902, 'Critical problems in the development of scientific thought, studied as vehicles for understanding the content and structure of the sciences. Specific subject matter in selected scientific disciplines will be presented. Students may not take this course on a P/NP basis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '104gw', 'Thematic Option Honors Program', 'David Albertson', 1192279, 'Analysis of historical change; social and political theory and revolutionary thought; introduction to competing images of future states of affairs; the continuing process of change. Students may not take this course on a P/NP basis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CORE', '112', 'Thematic Option Honors Program', 'Kyunghee Eo', 'Students may not take this course on a P/NP basis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '200', 'Liberal Arts Reading Salon', 'Trisha Tucker', 957779, 'Critical readings of a series of texts in the liberal arts designed to promote discussion of important themes, theoretical approaches, research directions, and interdisciplinary connections. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '301', 'Modes of Inquiry', 'Michael Petitti', 2019105, 'Modern tools of cultural and discursive analysis which seek to demystify "the natural," as it appears in the formation of cultures, their institutions, and individuals.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CORE', '450', 'A Polymathic Inquiry', 'William Deverell,Karin Huebner', 'A guided polymathic inquiry into Los Angeles as fact and imaginative creation, featuring in-depth student research and projects.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '499', 'Special Topics', 'Adlai Wertman', 2207621, 'Intensive interdisciplinary exploration of a selected theme, problem, process, or period.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CORE', '601', 'Teaching Analytical Writing through Readings in the Humanities', 'Trisha Tucker', 957779, 'Theories and practices in university-level teaching of close-reading and analytical writing, using texts central to Western tradition. Graduate student professionalization throught topical workshops and discussions. Open to assistant lecturers and teaching assistants only. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSLC', '503', 'Introduction to Comparative Studies in Culture', 'Ronald Mendoza-De Jesus', 'Examination of culture as an instrument of discursive practice that shapes social formations in Asia, Europe, North and Latin America.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSLC', '600', 'Publication', 'Erin Graff Zivin', 1800852, 'Preparation of book and article manuscripts for publication and placement in presses and journals; revising dissertations for publication; preparing papers for conferences. Students produce an article manuscript ready for submission to a journal. Duplicates credit in former COLT-603. Open only to doctoral students.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSLC', '601', 'Seminar in Comparative Media Studies', 'Akira Mizuta Lippit', 'Intensive comparative study of visual and literary media.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSLC', '640', 'Seminar in Film and Visual Studies', 'Panivong Norindr', 339504, 'Intensive study of various types of discourse (film, photography, literature) and their intersection with wider social, political, and theoretical issues.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '101x', 'Conversational Chinese and Intercultural Communication', 'Christopher Magriney', 1513382, 'Basic Mandarin conversational skills for effective communication in familiar, everyday Chinese contexts and better understanding of intercultural communication through content-based language acquisition. Graded CR/NC. Not available for credit to East Asian Area Studies, East Asian Languages and Cultures, and East Asian Studies majors and minors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '102', 'Calligraphy', 'Hsiao-Yun Liao', 1716232, 'This course introduces students to the origin of the basic components of Chinese scripts and the basic principles and styles of calligraphy.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EALC', '103a', 'Fundamental Chinese I', 'Xianxian Fang', 'Basic listening, speaking, reading and writing abilities in Mandarin Chinese.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '103b', 'Fundamental Chinese I', 'Hsiao-Yun Liao', 1716232, 'Basic listening, speaking, reading and writing abilities in Mandarin Chinese.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '104', 'Chinese I', 'Xiang Jian', 2061247, 'The sound system of modern Chinese; aural comprehension, oral expression, basic patterns, and writing system.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EALC', '106', 'Chinese II', 'Tin-Yu Tseng', 'Dialogue practice and conversation; readings of simple stories and essays; comparison of Chinese and English grammar; writing of paragraphs.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '108', 'Reading and Writing Chinese', 'John Chang', 2183042, 'The basics of reading and writing modern Chinese; intensive reading and writing of paragraphs, essays and stories; extensive reading of beginner-level authentic materials.', 4);
