INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SLL', '330gp', 'Russian Thought and Civilization', 'Sally Pratt', 'Russian cultural identity from its beginnings until today. The Eastern Orthodox tradition, its traumatic confrontation with Western culture, and their continuous interaction.', 4);
