INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SLL', '397', 'Literature and Film in Eastern European Historical Experienc', 'Anna Krakus', 1773055, 'Exploration of key moments in the recent historical experience of Eastern Europe through close readings of literature and film. Taught in English.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SLL', '546', 'The Russian Novel', 'Thomas Seifrid', 971694, 'Genre of the novel as exemplified in the works of one or more Russian authors. Readings from Gogol, Turgenev, Tolstoy, Dostoevsky, and others. Prerequisite: three years of college Russian.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SLL', '550', 'Russian Modernism', '', '"Russian Modernism" subsumes theoretical ideas, practical achievements, and stylistic movements which contributed to Russia\'s cultural renaissance between ca. 1890 and ca. 1925. Prerequisite: SLL 340.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SLL', '650', 'Seminar in Russian Literature', 'Kelsey Rubin-Detlev', 2398182, 'Detailed study of single literary period, movement, or genre; two or more selected authors; specific school of literary criticism. May be repeated, with departmental permission, if content of the seminar is different. Recommended preparation: one year of graduate study.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '142mg', 'Diversity and Racial Conflict', 'Leland Saito', 703313, 'Introduction to the causes and effects of contemporary race relations in a diverse U.S. society. Exploration of racial conflict at the personal and institutional levels.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '155mg', 'Immigrant America', 'Jody Agius Vallejo', 'Examination of the immigrant experience in the United States. Comparative analysis of social context of migration, formation of immigrant communities, and social integration of immigrants.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '169mg', 'Changing Family Forms', 'Tim Biblarz', 902243, 'The peculiarity of the "modern" Western family system in historical and cross-cultural perspective; focus on the "postmodern" family crisis in the United States.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '200mg', 'Introduction to Sociology', 'Juliana Mc Gene', 'Basic concepts of sociology with special reference to group life,social institutions, and social processes.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '210g', 'Science, Technology, and Social Conflict', 'Dan Lainer-Vos', 'Science and technology change society and how we understand ourselves. In turn, social struggles influence science. We will explore the interplay between these forces.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '242g', 'Sociology, Demography, and Health', 'Karen Sternheimer,Lynne Casper', 'Sociological and demographic concepts focusing on the causes of disease, health and wellness. Empirical analysis of population composition and its connection to health.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '270', 'Sociological Theory', 'Joseph Palacios', 2076594, 'Historical and contemporary approaches to sociological theory; analysis of conceptual frameworks applied to the study of society and social interaction.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '313L', 'Sociological Research Methods', 'Joseph Palacios', 2076594, 'Logic of theory construction, research design, elementary data collection and analysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '314Lg', 'Analyzing Social Statistics', 'Juliana Mc Gene', 'Sociological measurement, univariate description, elementary correlation, introduction to statistical inference.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '325', 'Applied Social Research Methods', 'Juliana Mc Gene', 'Use quantitative or qualitative analysis skills to study major social issues using large existing survey data sets or qualitative methods. Become conversant in describing data to a variety of audiences.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '340', 'Bureaucracy and Alternatives to Bureaucracy', 'Michael Siciliano', 'Importance of organizations in social life; techniques for using and changing organizations; examination of strategies for building and sustaining nonbureaucratic organizations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '342m', 'Race Relations', 'Elaine Bell Kaplan', 'Past and present relations between the White majority and the "conquered minorities" (Blacks, Chicanos, American Indians), as well as Asian immigrants; conflict vs. assimilation perspectives.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '351', 'Public Policy and Juvenile Justice', 'Karen Sternheimer', 1348354, 'Past and current theories of youth crime; gangs and other forms of youth deviance; the changing response of the police, courts, and public to these behaviors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '355m', 'Immigrants in the United States', 'Jody Agius Vallejo', 'Social construction of historical and contemporary immigration to the United States, including causes of migration, immigration policies, and the socioeconomic integration of immigrants.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '356m', 'Mexican Immigrants in Sociological Perspective', 'Pierrette Hondagneu-Sotelo', 'Effects of class, global inequality, legal status, gender, racial/ethnic, and language differences in distinguishing Mexican immigrant populations from the U.S. -born population; differentiation among Mexican immigrants.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '360m', 'Class, Status, and Power', 'Karen Sternheimer', 1348354, 'Inequalities in wealth, prestige, and power in the United States; the American class structure and the extent of upward mobility in that structure.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '431', 'Cities', 'Leland Saito', 703313, 'Organization of urban society, including such topics as segregation, urban decay, local politics, residential change, and community conflict.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '432m', 'Racial and Ethnic Relations in a Global Society', 'Nicole Hirsch', 'Examines race/ethnic relations with U.S. and selected countries from a global perspective, causes/social effects of globalization on people\'s lives, U.S. attitudes, and political policies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '450', 'Non-Governmental Organizations/Non-profits Field Practicum', 'Joseph Palacios', 2076594, 'Internship in a Non-Governmental Organization (NGO). Students will conduct sociological research on issues surrounding NGOs and the work they do.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '475', 'Medical Sociology', 'Josh Seim', 'Social and cultural factors in causation of disease, health care utilization and health care delivery.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '511', 'Sociological Theory II', 'Rhacel Salazar Parrenas', 'Developments in sociological theory from World War II to the present. Duplicates credit in former SOCI 610.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '520', 'Qualitative Research Methods', 'Paul Lichterman', 1423678, 'Seminar in epistemologies, ethics, and techniques of qualitative research. Critical reading and practice in social observation, interviewing, fieldwork, and research design. Preparation of IRB proposal.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '621L', 'Quantitative Methods and Statistics II', 'Dan Schrage', 2362418, 'Casual modeling and the inter-relationships among social phenomena: covers the basic elements of casual inference and generalizability, linear regressions analysis, and categorical data analysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '653', 'Seminar in Science and Technology Studies', 'Andrew Lakoff', 1417648, 'Introduction to key concepts and theories in the interdisciplinary field of Science and Technology Studies. Duplicates credit in former SOCI 532.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '657', 'Sociology of Culture', 'Paul Lichterman', 1423678, 'Cultural theories and forms of cultural analysis appropriate for sociological research; critical examination of theory and research on how culture relates to social structure, social inequality, politics, institutions, and everyday interaction. Recommended preparation: SOCI 510 or prior undergraduate or graduate coursework in social science or communication studies. Duplicates credit in former SOCI 535.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOCI', '675', 'Seminar in Immigration', 'Pierrette Hondagneu-Sotelo', 'Survey of key theoretical approaches and relevant issues in immigration studies. Themes include: transationalism, globalization, gendered migration, segmented assimilation, immigrant labor markets, social incorporation and citizenship. Open to Ph.D. in Sociology students only. Duplicates credit in former SOCI 575.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOCI', '680', 'Writing for Publication in Sociology', 'Ann Owens', 1914308, 'Prepares graduate students to publish in peer reviewed journals. Prerequisite: SOCI 620 or SOCI 622. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SPAN', '120', 'Spanish I', '', 'For students with limited proficiency in Spanish. Practice in listening comprehension, oral communication, elementary reading and writing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '150', 'Spanish II', 'Marie Enright', 1589978, 'Continuation of Spanish 110 or 120; increased emphasis on listening comprehension, oral expression, reading and writing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '220', 'Spanish III', 'Nathalie Bragadir', 1946849, 'Continuation of SPAN 150; intensive work in listening comprehension, oral communication, reading, and writing with emphasis on free expression; readings related to Hispanic culture and civilization. Prereq: SPAN 150 or equivalent.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '240', 'Spanish IV', 'Andrea Parra', 1039516, 'Intensive review of Spanish grammar with emphasis on four skills. Audio-visual materials and readings related to Hispanic culture and civilization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '260', 'Arts and Sciences', 'Karen Perez', 2001773, 'Development of students\' oral and writing skills using literary and scientific materials; grammar review. Duplicates credit in the former SPAN 266.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '261', 'Society and the Media', 'Lori Mesrobian', 1788016, 'Analysis of cultural issues in the Spanish-speaking world. Discussions, presentations, writing assignments, and grammar instruction designed to improve students\' proficiency in Spanish. Duplicates credit in the former SPAN 265.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '270', 'Spanish for Native Speakers', 'Vianey Cabrera', 1627028, 'For speakers with an advanced level of oral proficiency, but no previous formal study of Spanish. Focus on grammar, spelling and punctuation, reading, and writing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '280x', 'Conversational Spanish', 'Sarah Portnoy', 1525805, 'Discussions of short films, cultural and literary texts and other activities designed to improve conversational skills. Prerequisite: SPAN 220. Not for credit for Spanish majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '301', 'Introduction to Literature and Film', 'Lacey Schauwecker', 2297910, 'Introduction to critical reading and interpretation of poetry, narrative fiction, drama, and film from Spain and Latin America.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '304', 'The Art of Fiction', 'Lacey Schauwecker', 2297910, 'A survey of Spanish and Latin American fiction from the Middle Ages to the present, acquainting students with various critical and theoretical approaches to narrative. Conducted in Spanish.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SPAN', '308', 'The Art of Poetry', 'Camila Moreiras-Vilaros', 'A survey of Spanish and Latin American poetry from the Middle Ages to the present, acquainting students with various critical and theoretical approaches to verse. Conducted in Spanish.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '310', 'Structure of Spanish', 'Marianna Chodorowska-Pilch', 1538532, 'A systematic study of the structure of Spanish. Topics include fundamental aspects of the sound system; word classes; sentences and their meaning; linguistic change and variation; standard and colloquial usage.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '316x', 'Spanish for the Professions', 'Sarah Portnoy', 1525805, 'The language and culture of a particular area of study or profession, such as medicine and healthcare, political and social sciences, business and the law. Limited to 4 units for major or minor credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '385', 'The Culture of Food in Hispanic Los Angeles', 'Sarah Portnoy', 1525805, 'Experiential learning and project-based course designed to familiarize students with the food culture of Hispanic Los Angeles. Students create Spanish language blogs about their experiences.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '412', 'Spanish Rhetoric and Style', 'Marianna Chodorowska-Pilch', 1538532, 'Close grammatical and rhetorical analysis of a variety of text types (general, literary, technical, journalistic) as the basis for practice in advanced written and oral expression as well as translation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SPAN', '483', 'Gender and Sexuality', 'Ronald Mendoza-De Jesus', 'An examination of gender, sexuality, and power in Iberian and Latin American literatures and cultures. Conducted in Spanish.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SPAN', '499', 'Special Topics', '', '', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '501', 'Cultural Narratives of Spain and Latin America', 'Roberto Diaz', 1168906, 'Theoretical and methodological approaches to cultural narratives in Spanish and Latin American literary and cultural studies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SPAN', '592', 'Practicum in Teaching Spanish', '', 'Approaches and techniques in the teaching of Spanish and/or Portuguese as a second language.Open only to Master and Doctoral students in Comparative Literature, Comparative Culture in Literature and Studies (Spanish and Latin American Studies), Linguistics (Hispanic Linguistics) and Spanish.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SPAN', '602', 'Seminar in Spanish and Latin American Critical Theory', 'Erin Graff Zivin', 1800852, 'Major developments in literary criticism in Spain and Latin America from the early modern period to the present.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '135g', 'Maps in the Digital World', 'Su Jin Lee', 'The role of formal reasoning, abstract representation and empirical analysis in building maps for sharing knowledge across the physical, life and social sciences and humanities.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SSCI', '165Lgw', 'Sustainability Science in the City', 'Robert Vos', 2082054, 'The influence of sustainability science on public policy and vice versa in the context of social/ethical theories, analytical methods and solutions.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SSCI', '214g', 'Human Populations and Natural Hazards', 'Darren Ruddell', 1819302, 'Introduction to the complex relationship between human development and natural hazards, which are increasingly causing damage and displacement to human populations throughout the world.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '265Lg', 'The Water Planet', 'John Wilson,Elisabeth Sedano', 'An exploration of earth\'s water, ranging from water properties, chemistry, and pollution, to groundwater dynamics, watershed processes, and oceanic-atmospheric circulation. Implications for past and future societies. Lecture and laboratory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '301L', 'Maps and Spatial Reasoning', 'Elisabeth Sedano', 'Role of maps and spatial reasoning in the production and use of geographic information for representing and analyzing human and environmental activities and events.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SSCI', '340', 'Community Health Scan', 'Darren Ruddell', 1819302, 'The social context of health and its determinants; the uses of data analysis; community capacity building, and the impact of policy on community health improvement.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '382L', 'Principles of Geographic Information Science', 'Su Jin Lee', 'The various ways in which geography can be used to acquire, represent, organize, analyze, model and visualize information. Laboratories are organized around ArcGIS software suite.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SSCI', '383L', 'Geospatial Modeling and Customization', 'Jennifer Swift', 1547396, 'Fundamentals of spatial modeling and remote sensing and how to use GIS customization and programming to streamline complex spatial analysis and modeling workflows.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '412', 'GeoDesign Practicum', 'Travis Longcore', 'Application of design concepts, planning protocols and spatial analysis skills to a complex planning or design problem sponsored by a local public, private or not-for-profit client in a studio setting.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '580', 'Spatial Computing', 'Andrew Marx', 'Theoretical foundations, methods, techniques, and software systems for spatial computing, including geospatial semantic web and linked data, spatial data mining, geocoding, and document linking. Recommended preparation: SSCI 581 or INF 510.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '581', 'Concepts for Spatial Thinking', 'Katsuhiko Oda', 'The unique characteristics and importance of spatial information as they relate to the evolving science, technology, and applications of Geographic Information Systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '583', 'Spatial Analysis', 'Su Jin Lee', 'Provides the knowledge and skills necessary to investigate the spatial patterns which result from social and physical processes operating at or near the Earth\'s surface. Recommended preparation: SSCI 581. (Duplicates credit in former GEOG 583)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '683', 'Principles of Spatial Data Analysis', 'An-Min Wu', 'The theoretical foundations and techniques of spatial analysis and the ways in which they have been used to identify spatial processes and patterns. Recommended preparation: Enrollment in a USC PhD Program.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '684', 'Spatial Modeling with GIS', 'Laura Loyola', 'An introduction to spatial models identifying the key issues influencing the success of these models in simulating key social and environmental processes and health exposures.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('VISS', '599', 'Special Topics', 'Nancy Lutkehaus,Jenny Chio', 'Focuses on a comparative investigation of ways in which people in the past used images to make history by recording current events and subsequently use them to create narratives about the past.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('WRIT', '095x', 'Writing Tutorial', 'Cory Nelson', 1941911, 'Individualized instruction in writing to support instruction in WRIT 130 or WRIT 140. Graded CR/NC. Not available for degree credit. Concurrent enrollment in WRIT 130 or 140.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('WRIT', '120', 'Introduction to College Writing', 'Lauraanne Carroll-Adler', 'Intensive instruction and practice in the writing process. Focuses upon the formal conventions and conceptual expectations of college writing, with emphasis upon the grammatical, stylistic, and rhetorical techniques required in successful writing. Graded CR/NC. Limited to and required of students who score below specified level on the USC Writing Examination.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('WRIT', '150', 'Writing and Critical Reasoning--Thematic Approaches', 'Amber Foster', 2079526, 'Academic writing, emphasizing analysis and argumentation, rhetorical judgment, critical reasoning, creative insight, the careful use of evidence, ethical perspectives, logical organization, stylistic and grammatical fluency. Duplicates credit in WRIT 130 and WRIT 140.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('WRIT', '340', 'Advanced Writing', 'Carlos Antonio Delgado', 'Instruction in writing for various audiences on topics related to a student\'s professional or disciplinary interests, with some emphasis on issues of broad public concern. Prerequisite: WRIT 130 or WRIT 150.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('WRIT', '440', 'Writing in Practical Contexts', 'Michael Bunn', 1730569, 'Advanced training in analytical and argumentative writing for particular purposes in professional and practical contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '370', 'External Financial Reporting Issues', 'Smrity Randhawa', 1865793, 'Understanding of decision-making, problem solving, and research skills as a supplement to financial accounting knowledge for accounting professionals. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '371', 'Introduction to Accounting Systems', 'Mike Lee', 2075536, 'Understanding of technology and controls needed to capture data used by professionals in financial and managerial accounting, auditing and taxation. Prerequisite: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305. Duplicates credit in former ACCT-371ab.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '372', 'Internal Reporting Issues', 'Cecil Jackson', 421821, 'Understanding of decision-making, problem solving, and research skills as a supplement to managerial accounting knowledge for accounting professionals. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '373', 'Introduction to Auditing and Assurance Services', 'Rose Layton', 727533, 'Exploration of the requisite skills and knowledge needed to offer services in assurance, attestation or auditing engagements. Prerequisites: ACCT 370 and ACCT 371.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '374', 'Introduction to Tax Issues', 'Charles Swenson', 1933196, 'Basic tax principles, introduction to U.S. federal, state and local tax systems, income and expense definitions, property transactions, and fundamentals in individual taxation. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '377', 'Valuation for Financial Statement Purposes', 'Tom Ryan', 'Explores Accounting Standards Codification (ASC) 820 Fair Value Measurements and Disclosures and reviews the historical background of US GAAP fair value guidance. Prerequisite: ACCT 370.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '380x', 'Introduction to Enterprise Risk Management', 'Kristen Jaconi', 'Understand the concept of risk, risk assessment and management frameworks; learn to identify, analyze, manage and mitigate and communicate this risk to stakeholders.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '410x', 'Foundations of Accounting', 'Tracie Majors', 'Non-technical presentation of accounting for users of accounting information; introduction to financial and managerial accounting. Not open to students with course credits in accounting. Not available for unit or course credit toward a degree in accounting or business administration.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '415', 'Intermediate Financial Accounting for Non-Accounting Majors', 'Tom Ryan', 'In-depth study of balance sheet, income statement, and cash flow statement issues from the perspective of a user (not preparer) of corporate financial reports. Prerequisite: ACCT-410 or BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305. Not open to accounting majors. Duplicates credit the former ACCT-411x and ACCT-412x.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '416', 'Financial Reporting and Analysis', 'James Leonetti', 2394705, 'Examination of the role of financial statement analysis in the prediction of a firm\'s future financial performance.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '430', 'Accounting Ethics', 'Lee Cerling', 360745, 'Theoretical knowledge and practical application accountants need to identify ethical issues and reconcile conflicts among competing stakeholders\' interests in all major areas of accounting practice.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '462', 'Detecting Fraudulent Financial Reporting', 'Cecil Jackson', 421821, 'Understanding/identifying methods of fraudulent financial reporting, and signals that financial statements were fictitiously prepared; major frameworks for analyzing ethical dilemmas; reforms in corporate reporting environment.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '470', 'Advanced External Financial Reporting Issues', 'Bill Holder', 'Develop capabilities to identify and resolve advanced external financial reporting challenges, focusing primarily on operating, financing, and investing activities of business enterprises. Duplicates credit in ACCT 470ab.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '473', 'Financial Statement Auditing', 'Rose Layton', 727533, 'Course builds on the background developed in ACCT 373, specifically the process used by external auditors to conduct financial statement audits. Prerequisites: ACCT-373.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '474', 'Tax Issues for Business', 'Shiing-Wu Wang', 1437562, 'Capabilities to identify and articulate tax issues related to a business entity\'s life: formation, investing, financing and operations, and change of form.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '493', 'Leventhal Honors Research Seminar', 'Julie Suh', 2058533, 'Provides the methodological tools to identify research problems, develop researchable hypotheses, apply appropriate methodologies, conduct research, derive meaningful conclusions from data, write a research proposal.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '499', 'Special Topics', 'Kristen Jaconi', 'Selected topics reflecting current trends and recent developments in the field of accounting. Topics vary each semester.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '526', 'Global Accounting Experience', 'Shiing-Wu Wang,T J Wong', 'Cross-border transactions in the global economy examining accounting, legal, and tax environments, economic and political systems, and cultural differences. Includes international travel to selected region.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '530', 'Ethics for Professional Accountants', 'Gregory Kling', 'Provides the ethical grounding that accountants need to identify ethical issues and reconcile conflicts among competing stakeholder interests. Open only to Accounting and Business majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '532', 'Financial Accounting for Mergers and Acquisitions', 'George Braunegg', 'Practical problems in accounting for business combinations. Consolidated financial statements: fair value, acquisition and equity basis accounting. Duplicates credit in former ACCT 582.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '533', 'Tax Planning and Strategy', 'Shiing-Wu Wang', 1437562, 'Tax planning and strategy in corporate restructuring including mergers, acquisitions, and divestitures. Tax background not required to be successful in this course. Duplicates credit in former ACCT 582.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '537', 'Performance Measurement, Evaluation, and Incentives', 'Xin Li', 'Financial responsibility centers, financial and nonfinancial performance measures, budgets and other targets, evaluation techniques and styles, incentives. Strategy implementation and execution. Corporate governance. Online registration limited to graduate accounting and business students.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACCT', '551t', 'Taxation of Partnerships and S-Corps', 'Patricia Mills', 2396300, 'Federal taxation of flow-through entities, including:partnerships, S corporations, limited liability partnerships(LLPs), and limited liability companies (LLCs). Recommended preparation: ACCT-550T or ACCT-560T or LAW 600.', 3);
