INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACCT', '558', 'Advanced Accounting Valuation', 'Tony Aaron', 'Explores complex valuation issues arising in financial reporting and the related professional standards and guidance. Prerequisite: ACCT 528.', 1);
