INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARCH', '537', 'Environmental Perspectives', '', 'Principles and concepts of plant ecology for urban planting design; introduction to California native plant species and communities with field trips and case studies.', 3);
