INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARCH', '542bL', 'Landscape Architecture Design', 'Alex Robinson', 'Projects for the public realm with emphasis on urbanity and connectivity, place and meaning.', 6);
