INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACAD', '182', 'Case Studies in Innovation', 'Steve Barth', 2104210, 'Employing a case-study methodology, students analyze the artistic, technological, and entrepreneurial factors and address the conceptual, ethical, and logistical issues that lead to disruptive innovation. Prerequisite: ACAD 181. Open only to Arts, Technology and the Business of Innovation students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACAD', '187', '3D Design', 'Aaron Siegel', 2363966, 'An eight-week course covering the basics of industry-standard 3D modeling, rendering, and capturing software used for product, packaging, industrial and environmental design. Open only to Arts, Technology and the Business of Innovation majors.', 2);
