INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACAD', '324g', 'Ideation to Innovation', '', 'History and application of proven collaborative design processes and methodologies as they relate to ideation and innovation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACAD', '345', 'Product Design II', 'Grant Delgatty', 'A thorough look into developing a feasible consumer product through implementing all aspects of the R&D; process, including consumer insights, competitive analysis, ideation, and manufacturability.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ACAD', '360', 'Problem Solving for Organizations', 'Steve Barth', 2104210, 'A professional, collaborative and multidisciplinary consulting approach to diagnosing problems and applying design-based solutions on behalf of organizations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACAD', '377', 'Creation and Implementation', 'Stephen Child', 'Students create and implement-- from conception through prototype-- new ventures, products, services, and processes; building upon the innovations of designers, engineers, and entrepreneurs. Prerequisite: ACAD 376. Open only to Arts, Technology and the Business of Innovation students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ACAD', '475b', 'The Garage Experience', 'Matthew Manos', 'Academy capstone experience, innovative projects leading to operational prototypes and viable enterprises, mentored by faculty and industry experts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '510', 'Integrative Practices Residential', 'Davina Wolter', 'One-week campus residential. Introduction to program themes, content, concepts, theories, and processes. Opportunity to build skills and meet cohort, with an emphasis on team building. Open only to Integrated Design, Business and Technology majors. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '515', 'Professional Practices Residential', 'Sue-Tze Tan', 'One-week off-campus residential. Applied, intensive workshop format focused on solutions for real-world problems with input from industry experts.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '520', 'Design Essentials', 'Estevan Benson', 'Overview of design language, process and product. Build fluency in applying core visual principles and design-based problem-solving techniques. Explore historical and contemporary design issues. Recommended preparation: IDSN 510. Open only to Integrated Design, Business and Technology majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IDSN', '525', 'Business Essentials', 'Steve Barth', 2104210, 'Overview of general business and management concepts and techniques that builds theoretical and analytical fluency and identifies and examines leadership and managerial challenges. Recommended preparation: IDSN 510. Open only to Integrated Design, Business and Technology majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '530', 'Technology Essentials', 'Brian Boctor', 'Overview of core information technology concepts, methods, and frameworks. Develop aptitude in using tools, applications and systems; learn basic programming languages and methodologies. Recommended preparation: IDSN 510. Open only to Integrated Design, Business and Technology majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IDSN', '540', 'Processes and Perspectives', 'Michael Crowley', 18806, 'Discipline-specific, cross-disciplinary, and shared approaches to products, services, projects, systems, organizational development. Topics include creative and user-focused perspectives, feasibility analytics, lean startup techniques.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '545', 'Integrative Project', 'Grant Delgatty,Wade Cleveland', 'Faculty-mentored project paired with a Core course. Demonstrate individual and simultaneous skills and competency across fields and within cross-functional, cross-disciplinary teams. Recommended preparation: IDSN 510. Open only to Integrated Design, Business and Technology majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IDSN', '550', 'Opportunity and Uncertainty', 'Arpi Mardirossian', 1645680, 'Discipline-specific, cross-disciplinary, and shared approaches to recognizing/analyzing opportunities, identifying/framing problems, and developing/iterating solutions. Techniques gleaned from entrepreneurship, creative practices, critical thinking methods, ethnography, systems theory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IDSN', '560', 'Narrative and Storytelling', 'Larry Jordan', 2212479, 'Discipline-specific, cross-disciplinary, and shared approaches to exploring visual, written, and spoken storytelling using digital modalities. Topics include communication theory, narrative structures and content creation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IDSN', '570', 'Decisions and Solutions', 'Davina Wolter', 'Generate portfolio of solutions for a given "problem." Explore solutions and decisions, mechanisms for reducing uncertainty and risk. Includes quantitative and qualitative techniques.', 4);
