INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '424', 'Business Forecasting', 'Robertas Gabrys', 1491514, 'A variety of forecasting techniques used by a variety of businesses. Emphasis on learning to apply these techniques to real data. Prerequisite: BUAD-310. Duplicates credit in former DSO-424.', 4);
