INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DSO', '482', 'Supply Chain Management', 'Bala Subramanian', 'Issues in supply chain management. Supply chain performance and dynamics. Tools for planning, control and coordination. Supply chain design and strategy. Prerequisite: BUAD-311. Duplicates credit in former IOM-482.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '499', 'Special Topics', 'Inga Maslova', 2203734, 'Selected topics reflecting current trends and recent developments in data sciences, information systems, operations management, and statistics. May be repeated only if topic is different.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '510', 'Business Analytics', 'Richard Selby', 1413925, 'Foundational knowledge for business analytics, including strategies, methods, and tools integrated with hands-on skills for defining business analytics for data-driven decision making and innovation.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '516', 'Probability and Data Modeling', 'Dawn Porter', 977142, 'Principles of probability methodology. Application for providing structure to uncertainty. Develop, implement, and use probability models.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '522', 'Applied Time Series Analysis for Forecasting', 'Robertas Gabrys', 1491514, 'Survey of forecasting and time series methods. Models for stationary and nonstationary time series; ARIMA model identification, estimation, and forecast development. Seasonal and dynamic models. Web registration open only to graduate business and accounting students. Duplicates credit in former IOM 522. Recommended preparation: GSBA 506ab or GSBA 524 or (GSBA 516 and GSBA 545).', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '528', 'Data Warehousing, Business Intelligence, and Data Mining', 'Arif Ansari', 170468, 'Introduction to data-warehousing, multidimensional database, on-line analytical processing, and survey of business intelligence applications that extract useful information from data warehouses. Business applications emphasized.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '529', 'Advanced Regression Analysis', 'Dawn Porter', 977142, 'Computer-assisted analysis of business data; advanced multiple regression analysis, survey analysis, ANOVA testing for Marketing-type applications and Times Series Analysis methods will be covered. Prerequisite: GSBA-506b or GSBA-524 or (GSBA 516 and GSBA 545). Web registration open only to graduate business and accounting students. (Duplicates credit in former IOM 529)', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '530', 'Applied Modern Statistical Learning Methods', 'Xin Tong', 1798490, 'Overview of highly computational modern statistical learning methods; applications of logistic regression, neural networks, LASSO, trees, boosting and GAM, etc., to finance and marketing data.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '545', 'Statistical Computing and Data Visualization', 'Abbass Sharif', 1792749, 'Data cleaning and reshaping; good vs. bad graphics; univariate, bivariate, trivariate, hypervariate, and time series graphics; interactive graphics; web-related computing. Extensive computer applications using R.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '547', 'Designing Spreadsheet-Based Business Models', 'Hiroshi Ochiumi', 1515642, 'Application of decision analysis, simulation and optimization techniques to managerial problems. Learn how to create and present useful spreadsheet models to analyze practical business models. Online registration open only to graduate business and accounting majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DSO', '549', 'Application of Lean Six Sigma', 'Nick Vyas', 'Application of Six Sigma practices and techniques to improve operations in organizations. Open only to graduate business students. Duplicates credit in ISE 507 and former IOM 549.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DSO', '551', 'Digital Transformation in the Global Enterprise', 'Kyung Kyu Kim', 'Leveraging large enterprise system applications for strategic value; managing organizational transformation of global enterprises through digital business platforms; coping with disruptive technologies. Open only to business majors. Duplicates credit in former IOM-551.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DSO', '553', 'NoSQL Databases in Big Data', 'Arsames Qajar', 'NoSQL; semi-structured and unstructured databases; data storage; data manipulation; distributed databases.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DSO', '562', 'Fraud Analytics', 'Stephen Coggeshall', 2355142, 'Fraud detection model systems; identify normal vs. outlying behavior; malicious adversaries; complex datasets; supervised and unsupervised fraud statistical models; measures of model efficacy.', 3);
