INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DSO', '581', 'Supply Chain Management', 'Bala Subramanian', 'Issues in supply chain management. Supply chain performance and dynamics. Tools for planning, control and coordination. Supply chain design and strategy. Recommended preparation: GSBA 504b or GSBA 534. Duplicates credit in former IOM 581. Open only to business majors.', 3);
