INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('FBE', '443', 'Introduction to Forecasting and Risk Analysis', 'Mohammad Safarzadeh', 900975, 'Introduction to econometric tools and versions of Capital Asset Pricing Models to estimate financial risk, stock market risk premia and to project economic activity.', 4);
