INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('FBE', '572', 'Practicum', 'Jim Cunningham', 'This practicum emphasizes asset valuation, including applications of tools and inputs (including economics, accounting, and quantitative techniques) in asset valuation for those with prior experience. Duplicates credit in FBE 571 and FBE 573. Graded CR/NC.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('FBE', '573', 'Practicum', 'Jim Cunningham', 'This is an advanced practicum emphasizing portfolio management skills, including applied strategies (tools, inputs) in equity and fixed-income management for those with extensive prior experience. Duplicates credit in FBE 571 and FBE 572. Graded CR/NC.', 3);
