INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GSBA', '520x', 'Business Fundamentals for Non-Business Professionals', 'Merle Hopkins', 18909, 'An overview of concepts, tools and principles of business management to develop a general management point of view. Open only to non-business graduate students.', 3);
