INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GSBA', '555', 'Management and Organization of the Creative Industries', 'Mark Young', 2115591, 'How creative industries (motion pictures, television, publishing, radio, music, arts, games) operate and are organized. Critical discussion of pressing issues that these industries face.', 3);
