INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MKT', '402', 'Research Skills for Marketing Insights', 'Dinesh Puranam', 'Creating and interpreting qualitative and quantitative research to gain insight into marketplace challenges, such as testing advertising, identifying new product opportunities, and understanding customer decisions. Recommended preparation: BUAD 310 or COMM 301 or PSYC 274 or MATH 116. Duplicates credit in MKT 470.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MKT', '405', 'Advertising and Promotion Management', 'Arianna Uhalde', 2320743, 'Role of advertising in the marketing mix: determining objectives, strategies, and plans from situation analysis through research and creative processes, media selection, and sales promotion.', 4);
