INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MKT', '410', 'Professional Selling', 'Gary Frazier', 'Learn the principles of business to business selling and its function in marketing strategy and the marketing mix; explore professional selling as a career option. Prerequisite: BUAD 307 or MKT 385x. Open only to sophomores, juniors and seniors.', 4);
