INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MKT', '445', 'New Product Development and Branding', 'Therese Wilbur', 2111312, 'This class examines how new product ideas are developed, test marketed, and, ultimately, brought to the marketplace. Issues about why new products fail and how brand images are managed in the marketplace are discussed. Legal aspects of brand management such as trademark protection and infringement are also addressed. Prerequisite: BUAD 307 or MKT 385x.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MKT', '446L', 'Practicum in New Product Development', 'Therese Wilbur', 2111312, 'Provides experience in a student managed product team. Work with R&D organization to design a new product or technology. Involves market research and implementation planning. Prerequisite: MKT 445. Open only to juniors and seniors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MKT', '450', 'Consumer Behavior and Marketing', 'Lars Perner', 951790, 'Examines the relationship of consumer behavior to acquisition, usage, and disposition of products and the psychological, social and cultural influences that affect these decisions.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MKT', '465', 'Global Marketing Management', 'Dennis Schorr', 1352286, 'Product and service mix in multinational business; promotional alternatives; channel of distribution systems; pricing policies and legal barriers; multinational marketing opportunities; problems and information sources.', 4);
