INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '410', 'From Story Concept to Exhibition', 'Peter Exline', 1787084, 'Examination of the industry from story ideas, through script development, production and exhibition; evaluation of roles played by writers, agents, studio executives, marketing and publicity.', 2);
