INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '422', 'Makeup for Motion Pictures', 'Ian Goodwin', 1821155, 'Lecture-laboratory in makeup relating it to mood of the story and emulsion of the camera stock.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '423', 'Introduction to Special Effects in Cinema', 'Gene Warren Jr ,Gene Warren III', 'Introductory workshop in the aesthetics and practices of special effects, embracing both the classical and contemporary modes.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '424', 'Practicum in Cinematography', 'David Parrish', 'Camera and lighting workshop in color cinematography, beginning with digital still photography and moving into 16mm and advanced digital cameras. In-class exercises.', 2);
