INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '431', 'Developing the Documentary Production', 'Amanda Pope', 1680499, 'The tools and skills necessary to turn an idea into a documentary story, using sample reels, pitches, and writing to develop a professional proposal.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '438', 'Practicum in Producing', 'Michael Peyser', 'A comprehensive overview of the role of the producer. How projects are conceived, developed, packaged, financed and marketed.', 2);
