INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '448', 'World Building Design Studio', 'Peggy Weil', 'Integrating visual and scenario design in the construction of worlds as containers for narrative in documentary, drama, fiction, fantasy, and immersive media.', 2);
