INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '454', 'Acting for Film and Television', 'Anne Desalvo', 'Intensive examination of skills and techniques necessary for successful performances in film and television. Practical application through in-class exercises and assigned projects.', 4);
