INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '473', 'Spotting Music for Cinema', 'Daniel Carlin', 'A real world approach to spotting music for film: why film music exists and the many ways it is used in storytelling. For filmmakers as well as composers.', 2);
