INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '480', 'Advanced Production Workshop', 'Brenda Goodman', 1474826, 'Directors, producers, cinematographers, editors and sound designers collaborate to produce, shoot, edit and deliver fictional, documentary or experimental projects.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '486', 'Single Camera Television Dramatic Series', 'Gail Katz', 'Collaborative production and postproduction of an original episodic drama, shot on original sets on stage and on location.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '497', 'Music Video Production', 'Paula Nadine Walker,Rolf Kestermann', 'Writing the concept, budgeting, shooting, editing and directing a music video. Also covered: getting the job, dealing with the band, working with the record company.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '506', 'Visual Expression', 'Bruce Block', 121541, 'Exploring the relationship between story structure and visual structure, discussion of aesthetics and technical controls, in-class examples and screenings, photographic assignments.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '507', 'Production I', 'Angelo Pacifici,', 'The effective communication of ideas through the language of cinema; one directing exercise; two short HD projects; introductions to producing, directing, editing, cinematography, and sound.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '508', 'Production II', 'Scott Kroopf,', 'Practicum in group production, emphasizing the collaborative process and the expressive use of sound and image. Prerequisites: CTPR-507 and CTPR-510 and CTWR-505.', 6);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '522', 'Reality Television Survey', 'Scott Stone', 1875128, 'A comprehensive overview of the world of reality television; each student will develop and pitch an original reality-based program.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '523', 'Introduction to Multiple-Camera Production', 'Andrew Cadiff', 'How to direct comedy or dramatic scenes, using multiple camera techniques. Students also serve as crew members, learning lighting, mixing, studio controls, and stage management.', 2);
