INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '532', 'Intermediate Directing', 'Barnet Kellman', 1890571, 'Practical experience in staging dramatic narrative scenes, emphasizing directing actors, rehearsal techniques and camera blocking.', 2);
