INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '535', 'Intermediate Editing', 'Doug Blush,', 'Editorial construction of sequences to analyze the interrelationships of the various film elements, both visual and aural.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '537', 'Intermediate Cinematography', 'Skye Borgman', 'Close study through practical in class exercises of the technical and aesthetic principles of cinematography. Introduction to 16mm film and advanced digital cameras.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '538', 'Intermediate Producing', '', 'Definition, examination and practical experience in the role of the line producer as it relates to preproduction, production and post production.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTPR', '546L', 'Production III, Fiction', 'John Watson', 'An intensive workshop experience in which students, crewing in their area of specialization, complete the shooting and postproduction of projects up to thirty minutes in length. Qualifying courses: for directors, CTPR 532, and for cinematographers, CTPR 537.', 6);
