INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '552', 'Advanced Directing', 'Everett Lewis', 693342, 'An advanced production class in directing. Encounters with experienced directors; and individual student production of a short. Recommended preparation: CTPR 533.', 2);
