INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTPR', '555', 'Advanced Production Design', 'Bruce Block', 121541, 'The execution of a production designer\'s in-depth analysis and fully detailed breakdown of a feature-length script for story, character, sets, locations, set dressing, props, fabrics, color and texture.', 2);
