INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '413', 'Writing the Short Script I', 'Martin Daniel', 2371732, 'Preparation of scripts for short films: form, structure, planning. Open only to Cinematic Arts Film and Television Production majors.', 2);
