INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '418b', 'Senior Thesis', 'Peter Robinson', 1998395, 'b: Completion and revision of Senior Thesis project and introduction to motion picture industry procedures and practices through interaction with industry representatives. Open to B.F.A. Writing for Screen and Television students only.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '419b', 'Senior Thesis in Dramatic Television', 'Maria Escobedo', 2135634, 'Revision of original pilot script, writing of mid-season episode, and completion of bible for original dramatic series developed in first semester.', 4);
