INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '435', 'Writing for Film and Television Genres', 'Irving Belateche', 1822665, 'Preparation of proposals and scripts for different types of film or television programming: emphasis on conception, structure, characterization and format.', 2);
