INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '453', 'Advanced Feature Rewriting', 'Jeffrey Hammer', 'An advanced workshop in which a feature length screenplay will be rewritten using a specific methodology that focuses on a macro to micro approach to revision.', 4);
