INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '489', 'Writing and Producing the Episodic Drama', 'David Balkan', 1609916, 'Staff writing and producing the original episodic drama, from development to post-production.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '505', 'Creating the Short Film', 'David Weber', 1599831, 'Strengthening and deepening the ability to conceive and develop ideas that will lead to compelling, authentic, and personally meaningful films. Duplicates credit in CTWR 528.', 2);
