INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '515b', 'Practicum in Screenwriting', 'Mary Sweeney', 'Comprehensive re-writing of second and third drafts of a feature screenplay leading to a polished and professional piece.', 4);
