INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '518', 'Introduction to Interactive Writing', 'Maureen Yeager', 'A series of exercises written and discussed for interactive experiences.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '519b', 'Thesis in Television Drama', 'David Balkan', 1609916, 'Part B -- writing the series bible and mid-season episode -- is part two of the television thesis course. Students develop their original series further by writing the bible for the series and the mid-season episode which is reflective of storylines, characters and settings developed in part A of the course. Open to M.F.A. Writing for Screen and Television students only.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '520', 'Advanced Scene Writing Workshop', 'Theodore Braun', 'Intensive workshop oriented specifically to writing and re-writing the most effective and telling dramatic scenes to heighten audience participation and greater story impact.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '523', 'Introduction to the Screenplay', 'Robert Ramsey', 'Introduction to formal elements of the screenplay through lectures and the workshopping of a complete first draft of a feature-length script. Prerequisite: CTAN 536 or CTWR 505 or CTWR 518. Recommended preparation: CTWR 516.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '529', 'Intermediate Screenwriting', 'Siavash Farahani', 'Emphasizes structural elements crucial to the feature film. Techniques for creating an original or adapted theatrical-length script.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '533a', 'Writing the Feature Script', 'Donald Hewitt', 'Advanced screenwriting workshop. Development of characters and story to complete a treatment and first draft of a feature length script.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '533b', 'Writing the Feature Script', 'David Weber', 1599831, 'Advanced screenwriting workshop. b: Rewriting the first draft into a second draft through advanced work-shopping of script. A third draft polish is the final assignment.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '537', 'Advanced Half-Hour Comedy Series Pilot', 'Sonja Warfield', 2204805, 'Development of an original half-hour comedy series; writing of the pilot episode and series bible. Prerequisite: CTWR 514a. Open only to master students in Theatre (Dramatic Writing) and Writing for Screen and Television.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '539', 'Advanced Hour-Long Drama Series Pilot', '', 'Development of an original one hour drama series; writing of the pilot episode and series bible. Prerequisite: CTWR 514a. Open only to master students in Theatre (Dramatic Writing) and Writing for Screen and Television.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '549', 'Advanced Rewriting Workshop in Hour-Long Drama', 'Ligiah Villalobos', 'Advanced television writing, emphasizing the rewrite of a first draft pilot script and series bible for an hour-long television series.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CTWR', '550', 'Advanced Story Development', 'Howard Rodman', 1713460, 'Advanced development of the story creation process by examining the core elements of a good story. Compiling a portfolio of story ideas.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '553', 'Advanced Rewriting Workshop', 'Aaron Benay', 'Advanced feature screenwriting, emphasizing the rewrite of a first draft script.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '555', 'Pitching for Film and Television', 'Paul Foley', 'Development and pitching of ideas for motion pictures, episodic and television pilots. Reducing ideas to basic components; enhancing verbal presentation skills.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CTWR', '585', 'Advanced Genre Writing', 'Neely Swanson', 'Development and execution of writing in various genres of film, television, or new media scripts. Prerequisite: CTWR 514b.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '104', 'Introduction to Digital Studies', '', 'An introduction to the expressive range of screen languages in their cultural, historical, and technological contexts.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '140', 'Workshop in Multimedia Authoring', 'Tatiana Mulry', 'Introduction to the expressive potential of multimedia as a critical and creative tool, supplementing traditional forms of academic work.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '201', 'The Languages of Digital Media', 'David Johnson', 'An in-depth investigation of the close interrelationships among technology, culture and communication to form a solid foundation for digital authoring. Duplicates credit in former IML 101.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '203', 'Media Arts and Practice Studio II', 'Gabriel Peters-Lazaro,Evan Hughes', 'Intermediate laboratory exploring computational systems through a series of critical design prompts. Emphasis on digital representation, time-based processes, abstract data and the relationship between humans and computers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IML', '230', 'Fundamentals of Media Design', 'Evan Hughes', 1646464, 'Introduction to the methods of visual design with respect to digital media authorship. Exploring the creative process through various conceptual workflows and hands-on techniques.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '288', 'Critical Thinking and Procedural Media', '', 'Investigation of the potentials of computational media to define new aesthetics, modes of representation and structures of communication.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '295Lm', 'Race, Class and Gender in Digital Culture', 'Kiki Benzon', 'Critical analysis of the categories of race, class and gender within the diverse digital spaces of contemporary culture, from video games to the digital divide.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '300', 'Reading and Writing the Web', 'Eric Fanghanel Santibanez', 'An introduction to a broad range of technical and theoretical issues surrounding the production of web-based content.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '309', 'Integrative Design for Mobile Devices', 'Tatiana Mulry', 'Hands-on investigation of opportunities and challenges offered by mobile interaction within both cultural and ideological contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '310', 'Professionalism for Media Arts', 'Ashley York', 'Development of documentation and archival strategies, with an emphasis on techniques of personal and professional representation.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IML', '328', 'Sonic Media Art', 'Evan Hughes', 1646464, 'Survey of audio culture and sound technologies, focusing on developing sonic literacy and creating artwork using sound as a primary modality.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '335', 'Digital Narrative Design I', 'Ashley York', 'An introduction to audio and video techniques for digital storytelling across various platforms. Students will create multiple short-form projects using contemporary tools and technologies.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '346', 'Methods in Digital Research', 'David Johnson', 'Emphasizing rigorous multimedia research and authorship strategies, this course prepares students to undertake large-scale digital projects.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '354', 'Introduction to 3-D Modeling', '', 'An introduction to the history, theory and critical context of 3-D spatial representation, as well as foundational authoring skills in modeling interactive 3-D spaces.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IML', '365', 'Future Cinema', 'Holly Willis', 739322, 'Examination of the history of cinematic experimentation to provide a framework for understanding contemporary virtual reality, augmented reality, interactive installations and large-scale urban screens.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '385', 'Design Fiction and Speculative Futures', '', 'The history, theory and methods of design fiction, focusing on design videos and physical prototypes as tools for exploring contemporary social, political and ethical life.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '404', 'Tangible and Spatial Computing', 'John Carpenter,Pete Hawkes', 'Exploration of extended human computer interaction, including new forms of haptic, sonic and other sense modalities, through physical computing and spatial interfaces.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '419', 'Emotion in Digital Culture', 'Elizabeth Ramsey', 'Project-based course examining emotion in relation to technology, digital culture and the human experience.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '428', 'Exploring and Creating Sonic Environments', '', 'Exploration of spatial sound practices, focusing on sound installation art, sonic visualization and audio storytelling techniques.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '436', 'Hypercinemas Studio', 'Matthew Scott', 'A hands-on exploration of emerging cinematic technologies, situated within a historical context through both theoretical and practical analysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '440', 'Interdisciplinary Honors Thesis', 'Virginia Kuhn', 'Production of an interdisciplinary digital thesis project.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '444', 'Thesis Project II', 'David Johnson', 'Production of a digital thesis project. Prerequisites: IML 441. Open only to Media Arts and Practice majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '454', 'Advanced Techniques of Spatial Representation', '', 'Studio class exploring spatial perception and advanced techniques of 3-D representation, stereoscopy, panorama, projection mapping, modeling for real-time environments, parametric modeling, and 3-D fabrication.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IML', '456', 'Nature, Design and Media', 'Pablo Frasconi', 2398741, 'Investigation of the impact of natural patterns on digital media design. Explores the relationships among chaos, harmony, beauty, proportion, spirituality, holistic systems and shaped experience.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '475', 'Media Arts Research Lab', 'Todd Richmond', 'A hands-on mentored research lab experience within the context of media art and in association with a real-world project.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '477', 'Embodied Storytelling and Immersive Docu-Narratives', 'Michael Bodie', 'Examination of art, media, and theatre, to create an immersive, installation-based intervention utilizing the embodied 360-degree docu-narrative form.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '499', 'Special Topics', 'Anne Bray', 'Selected topics in multimedia literacy.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '501L', 'Digital Media Authorship and the Archive', 'Virginia Kuhn', 'An in-depth examination of emergent technologies in their cultural and historic contexts, with an equal emphasis on production and analysis. Open only to graduate students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '521', 'Non-Fiction Cinematic Practice II', 'Michael Bodie', 'Deepening skills in audio/video storytelling methods through a semester-long, non-fiction media project.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '535', 'Tactical Media Strategies', 'Virginia Kuhn', 'Integrating hardware, software and interactivity to explore new forms of expression. Emphasis on critical tactics (culture hacking, creative misuse) aimed at activism and social change.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '575', 'Graduate Media Arts Research Lab', 'Alexander McDowell', 'A hands-on mentored graduate research lab experience within the context of media art and in association with a real-world project. Open only to graduate students.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('IML', '600', 'Historical Approaches to Media Arts and Culture', 'Henry Jenkins', 1941955, 'Introduction to the historical specificities of old media (painting, print, photography, film, video, television) and to consequences of the convergences produced by new media forms. (Duplicates credit in former CNTV 600)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '602', 'Practice of Media Arts', 'Andreas Kratky', 'Introduction to a range of technologies and media types, while identifying and developing a specialization in one or more areas of practice. (Duplicates credit in former CNTV 602)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('IML', '603', 'Media Arts and Practice Colloquium/Professional Seminar', 'Andreas Kratky', 'Orientation to the profession, opportunities, presentation and analysis of recent developments and applications in Media Arts and Practice. Graded CR/NC. (Duplicates credit in former CNTV 603)', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASCJ', '200', 'Navigating Media and News in the Digital Age', 'Rebecca Haggerty,Alison Trope', 'Students will be engaged as discriminating media/news consumers and contributors. Emphasis on critical skills needed to understand, employ, enjoy and help shape our media landscape.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASCJ', '210', 'Contours of Change in Media and Communication', 'Mark Lloyd', 2247836, 'Provides an introduction to cultivating a curious, critical, and proactive approach to the challenges of social change. Students will be encouraged to develop an expansive and critical definition and understanding of change as it relates to the role of media and communication in producing and responding to political and cultural transformations that shape our status as citizens and our ideas of citizenship.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '501', 'Communication Management Pro-Seminar', 'Ben Lee,Brad Shipley,Rebecca Weintraub', 'Central issues of theory and practice in the management of communication; broad introduction to all areas of the program.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '505', 'Communication in Work Settings', 'Colleen Keough', 1761463, 'How work settings determine communication: basic structures of communication, influence of technology, social contexts, and physical space. Applications to management.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '507', 'Information Management', 'Liuning Zhou', 'Develops a conceptual framework for understanding information, uncertainty, knowledge, interpretation, and equivocality. Principles for managing information load, and communication networks information distribution, and decision making.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '510', 'Communication, Values, Attitudes and Behavior', 'Sheila Murphy', 'Theory and research on value and attitude formation and change; consequences for communication and behavior.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '520', 'Social Roles of Communication Media', 'Peter Clarke', 'How mass media shape public images of groups, channel political power, promote consumption of goods. Social and political theoriesas tools in evaluating media impact.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '528', 'Web Designs for Organizations', 'Frederick Nager', 'Students learn to assess organizations\' online needs, to examine the use of the Internet in terms of electronic commerce and global pressures, and design web page strategies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '531', 'Communication and the International Economy', 'Jonathan Aronson', 14898, 'Examination of the impact of global economic changes on communications industries, the political and economic forces shaping these industries and the roles of its managers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '537', 'The Industry, Science and Culture of Video Games', 'Dmitri Williams', 1712720, 'History, social dynamics, and cultural impact of video games; developments in technology and design; issues confronting the video game industry and organizations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '542', 'Business Strategies of Communication and Entertainment Firms', 'Frederick Nager', 'An overview of business strategies in media industries. Emphasis on the Internet and changing business models. Case studies. Students prepare business plans for digital start-ups.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '544', 'Meaning Through Messages', 'Simon Uwins', 'Use of rhetorical theories and communication models to create organizational identification with internal and external audiences; the role of values and ethics in creating identities.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '549', 'Case Studies in Digital Entertainment', 'Ariela Turndorf', 'Explores foundation of U.S. media policy in the digital age; students prepare White Papers on an urgent issue of contemporary digital media and entertainment policy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '552', 'Production, Management and Culture', 'David Craig', 1664681, 'Focuses on management, production and distribution of scripted film, television and web stories to understand visual storytelling as a communicative strategy for advertising and education.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '556', 'Global Marketing Communication', 'Paolo Sigismondi', 1787886, 'Communication strategies in a global marketing environment; analysis of global  local challenges and opportunities; effective global integrated communication to create and sustain competitive advantage', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '558', 'The International Entertainment Marketplace', 'Hernan Galperin', 'Global influences on entertainment industries (broadcasting, film, telecommunications, Internet, video games, and music); case analyses of specific organizations and geographic regions; impact on local cultures.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '576', 'Communication Strategies for Conflict Management', 'Colleen Keough', 1761463, 'Communication strategies for effective negotiation, mediation and facilitation of disputes; structures for public interventions; emergence of online dispute resolution systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '578', 'Non-profit Advocacy', 'Gary Wexler', 2003092, 'Examines non-profit advocacy (vs. for-profit communication) marked by different rhythmic and creative drivers; non-profit audience analysis; creation of conversations for viral communication impact.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CMGT', '586', 'Content, Theory, and Industry Practices', 'David Craig', 1664681, 'Examination of social scientific theory and research on patterns of media content; effects of mass media exposure on individuals and society; and industry practices.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '587', 'Audience Analysis', 'Mathew Curtis', 'Fundamental principles of audience research; critique of existing methodologies; implications for global audiences and mass media markets.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '597b', 'Communication Research Practicum', 'Daniela Baroffio', 'Students design and produce an original project appropriate for their emphasis area within the Master of Communication degree. Open to Master of Communication Management students only. (Duplicates credit in former CMGT 597).', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CMGT', '599', 'Special Topics', 'Mayanna Framroze', '', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '200', 'Communication and Social Science', 'Ken Sereno', 683669, 'Social scientific inquiry into human communication; core theories of message production and reception in interpersonal, group and organizational contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '204', 'Public Speaking', 'Christiana Robbins', 'Principles and practices of effective oral communication; analysis of the speaking-listening process; selection and organization of speech materials; use of new presentation technologies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '205x', 'Communication Practicum', 'Laurel Felt', 'Students address communication issues in a field setting. They will evaluate communication practices using appropriate methodology. Projects are jointly evaluated by internship supervisor and professor. Sophomore standing. Graded: CR/NC. Not for major credit for Communication majors.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '206', 'Communication and Culture', 'Cristina Visperas', 'Examines cultural institutions, ideologies, artifacts, and productions; role of culture in everyday life; cultural studies as methodology; culture and power.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '209', 'Communication and Media Economics', 'Ernest Wilson', 'Economic and political economic principles for the analysis of communication and media industries including broadcasting, newspapers, motion pictures, music, video games, advertising and public relations. (Duplicates credit in former COMM 207 and COMM 208)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '300', 'Entertainment, Communication and Society', 'Daniela Baroffio', 'Theoretical foundation for understanding the construction, consumption, and consequences of entertainment from classical to contemporary times; situates entertainment within the ecology of information and communication.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '301Lg', 'Empirical Research in Communication', 'Carmen Lee', 2077605, 'Experimental and survey methods for communication study; basic statistical concepts, procedures, and tests.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '302', 'Persuasion', 'Ken Sereno', 683669, 'Theories and research in social influence; strategies and tactics of persuasive communications in such settings as politics, public relations, advertising, business.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '304', 'Interpersonal Communication', 'Deborah Creech', 'Analysis of face-to-face interaction; role of communication in the development, maintenance and destruction of relationships; communication processes in managing interpersonal conflict.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '305', 'Understanding Social Science Research', 'Carlos Godoy', 1807848, 'Students learn to be consumers rather than creators of social science research. Examines the challenges and opportunities of communicating research through mass media.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '306', 'Innovation, Entertainment, and the Arts', 'James Lee', 'Explorations of innovation in the entertainment business. The effects of digital mobile media on TV, movies, music, advertising, social networks and art.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '309', 'Communication and Technology', 'Christina Dunbar-Hester', 2243530, 'Cultural, social, political, and economic dynamics of communication technologies, including internet architecture, social media, participatory cultures, privacy, surveillance, networked journalism, big data, algorithms, online activism. (Duplicates credit in former COMM 202)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '310', 'Media and Society', 'Robert Scheer', 156504, 'Interplay between media and society, including family and children\'s socialization, inter-group relations and community, pornography and violence, gender and race, media ethics, conduct of politics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '311', 'Rhetoric and the Public Sphere', 'Gordon Stables', 1583664, 'Humanistic approaches to inquiry in communication; qualitative research techniques; core theories of message production and reception in social, political, and mediated contexts. (Duplicates credit in former COMM 201)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '312', 'The Business and Culture of Celebrity', 'Nazli Senyuva', 'Employs the concept of "celebrity" as an optic through which to view and assess some of the key aspects of the communication revolution.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '313', 'Communication and Mass Media', 'Carmen Lee', 2077605, 'Survey of mass communication research; history, content, effects, theories, and policy implications of various media. (Duplicates credit in former COMM 203)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '320', 'Small Group and Team Communication', 'Julianna Kirschner', 'Group process theories relevant to communicative behavior in small group/team settings, including information exchange, decision making, leadership, and meetings; student team projects testing theoretic propositions.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '321', 'Communication and Social Media', 'Karen North', 1577333, 'Social media within the broader social, political, and historical contexts; key themes include labor, Web 2.0, self-branding, celebrity, participation, privacy, online shaming, and activism.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '322', 'Argumentation and Advocacy', 'Daniel Durbin', 476777, 'Basic argumentation theory including analysis. Research and evidence, case construction, refutation; discursive and visual argument; diverse fields of advocacy including law, politics, organizations, interpersonal relations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '324mw', 'Intercultural Communication', 'Jillian Pierson', 1813521, 'Cultural variables and social psychological processes that influence intercultural interaction; relationship between communication and culture in diverse settings including business, medicine, and education.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '336', 'Communicating About Sex', 'Jillian Pierson', 1813521, 'Communicating with partners, friends, parents and health care professionals about sexual behavior; interpersonal and mediated contexts; understanding of sexual scripts; current theory and research.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '340', 'The Cultures of New Media', 'Matthew Bui', 2204817, 'Cultural implications of computer-mediated communication and related media. Ideological responses to media innovation; debates over artificial intelligence, virtual communities, and virtual reality. Recommended preparation: COMM 339.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '355', 'Advertising and Communication', 'Rook Campbell', 2142504, 'Advertising as a mode of communication; US advertising history and institutions; economic and policy contexts (domestic and global); critical analysis of advertising texts', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '363', 'Media Consumption', 'Hye Jin Lee', 2095438, 'Theoretical approaches to the study of media consumption and audiences; examines international media and consumption practices; explores new media\'s impact on consumption.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '366', 'Designing Media and Communication Projects for Social Change', 'Alison Trope', 1285212, 'Students explore the theoretical and practical issues involved in designing effective media and communication projects for social change in international contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '367', 'Community Engagement and Service Learning', 'Rogelio Lopez', 'Students engage with a community partner, bridging classroom learning with "real world" experiences by working within the diverse community surrounding USC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '375', 'Business and Professional Communication', 'Karen North', 1577333, 'Oral and written communication skills demanded in the workplace including informative and persuasive speeches; interviewing; team communication; and training material preparation. Recommended preparation: COMM 204', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '380', 'Forensics Laboratory', 'Kiranjeet Dhillon', 'Directed individual research studies of contemporary problems. Supervised laboratory experience. Open only to members of the University debate squad.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '381', 'Issues in Contemporary Sport', 'Rook Campbell', 2142504, 'Explores social, political and ethical issues in elite sports and how issues are addressed through popular media; examination includes the relationship between sports and politics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '383m', 'Sports, Communication and Culture', 'Daniel Durbin', 476777, 'Rhetorical and critical approaches to sports and public discourse; application to sports organizations, the news and popular media; representations of gender and race in sports.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '384', 'Interpreting Popular Culture', 'Sarah Kessler', 'Popular culture as an indicator of cultural values, a producer and reflection of cultural meaning, and a means of communication; theory and case studies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('COMM', '387', 'Sports and Social Change', 'Julianna Kirschner', 'Application of critical, sociological and rhetorical theories to sports events and sport media; examination of the role of sports in enacting social change.', 4);
