INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '499', 'Special Topics', 'Dan Erwin', 'Course content to be selected each semester from current developments in astronautics, space technology, and related fields.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '505b', 'Plasma Dynamics', 'Joseph Wang', 'Charged particle dynamics. Kinetic and fluid theories. Rarefied and collisional plasma flows. Plasma-surface interactions. Waves, instabilities, turbulence. Applications in engineering and space science. (Duplicates credit in EE-572b)', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTE', '523', 'Design of Low Cost Space Missions', 'James Wertz', 830899, 'Reviews all aspects of space mission design for practical approaches to reducing cost. Examines "LightSat" mission experience and potential applicability to large scale missions. Graduate standing in engineering or science. Recommended preparation: ASTE 520 or some experience in space engineering.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '529', 'Safety of Space Systems and Space Missions', 'Michael Kezirian', 'Engineering methodology and analysis techniques for safety certification and mission assurance of robotic and human space systems and space missions by government and commercial industry. Recommended preparation: ASTE 520 or some experience in space engineering. Open only to Engineering graduate students.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '535', 'Space Environments and Spacecraft Interactions', 'Joseph Wang', 'Space environments and interactions with space systems. Vacuum, neutral and ionized species, plasma, radiation, micrometeoroids. Phenomena important for spacecraft operations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '566', 'Ground Communications for Satellite Operations', 'David Barnhart', 'Theory, practice, architecture, operations of ground satellite communications with satellites. Practical implementation of satellite communications system and reception and analysis of satellite transmitted signals.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '570', 'Liquid Rocket Propulsion', 'Ghanshyam Purohit', 'Liquid-propelled rocket propulsion systems. Capillary devices for gas-free liquid acquisition in zero gravity. Ground and in-orbit operations. Propellant life predictions and spacecraft end-of-life de-orbiting strategies.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTE', '572', 'Advanced Spacecraft Propulsion', 'Keith Goodfellow', 1097667, 'Nuclear, electric, sails, and far-term propulsion systems. Overviews of nozzles, heat transfer, electromagnetics, rarefied gases, and plasma physics. Analysis of electrothermal, electrostatic and electromagnetic thrusters. Graduate standing in engineering or science.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '574', 'Space Launch Vehicle Design', 'Don Edberg', 'Fundamentals, technologies, and design of space launch vehicles. Propulsion, trajectory analysis and optimization, static and dynamic structural loads, stability, control, and safety.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTE', '580', 'Orbital Mechanics I', 'Gerald Hintz', 649926, 'Physical principles; two-body and central force motion; trajectory correction maneuvers; position and velocity in conic orbits; Lambert\'s problem; celestial mechanics; orbital perturbations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '581', 'Orbital Mechanics II', 'Ryan Park', 'Theory of perturbations of orbits; numerical methods in orbital mechanics; satellite dynamics; averaging methods; resonance; mission analysis.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ASTE', '599', 'Special Topics', 'Garrett Reisman', 'Course content to be selected each semester from current developments in astronautics, space technology, and related fields.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '101', 'Introduction to Biomedical Engineering', 'Jesse Yen', 941677, 'Historical development and survey of major areas comprising biomedical engineering: theoretical neurobiology and systems physiology, biomedical instrumentation, artificial organ and prosthetic devices, biomedical computer applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '201', 'Biomedical Engineering Practice', 'Brent Liu,Christopher Noll', 'Examination of the technical and practical challenges involved in the development of medical devices, including neural implants, in industry and the clinical setting. Recommended preparation: BME 101.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '210', 'Biomedical Computer Simulation Methods', 'David D\'Argenio', 1920744, 'Computational methods for simulation of circulatory, respiratory, pharmacokinetic, and neural models. Quadrature, differential equations, systems of linear equations, simulation languages, experimental statistics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '302L', 'Medical Electronics', 'Jean-Michel Maarek', 'Electronic design and measurements for medical applications. Use of integrated circuits, biopotential measurements, static and dynamic calibration of physiological transducers.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '402', 'Control and Communication in the Nervous System', 'Bartlett Mel', 2131730, 'An introduction to the structural and functional elements common to nervous systems, with emphasis on cellular dynamics, interneuronal communication, sensory and effector systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '405L', 'Measurements and Instrumentation', 'Jean-Michel Maarek', 'Application of instrumentation and measurement techniques to biomedical engineering projects involving measurement, replacement or augmentation of biomedical systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '410', 'Introduction to Biomaterials and Tissue Engineering', 'Megan McCain', 2083951, 'Application of principles of physical chemistry, biochemistry, and materials engineering to biomedical problems, e.g., materials selection and design for implants and tissue replacement.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '416', 'Development and Regulation of Medical Products', 'Jerry Loeb', 'An introduction to the process of medical product development with emphasis on the regulations that govern the design, fabrication, and maintenance of medical products. Junior standing. Departmental approval required.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '453', 'Engineering Biomedical Innovations', 'Tuan Hoang,George Tolomiczenko', 'Engineering principles in design, modeling, and analysis of biomedical innovations will be presented to develop creative solutions for real-world medical problems or treatment implementation. Co-requisite: BME 405L. Recommended preparation: BME 416.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '506', 'Bioengineering of Disease and Cell Therapeutics', 'Keyue Shen', 2088566, 'Introduction to micro-/nano-technologies, microscopy, single-cell techniques, materials, surface chemistry, pathology, cancer, immunology, cell therapy, and grant writing in biomedical research.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '513', 'Signal and Systems Analysis', '', 'Classification; representation; statistical analysis; orthogonal expansions; least-squares estimation; harmonic analysis; Fourier, Laplace, and Z transforms; the linear system; filtering; modeling and simulation; linear control theory.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '523', 'Measurement and Processing of Biological Signals', '', 'Acquisition, analysis, and display of biological data using digital computers; laboratory applications of digital signal processing and real time analysis. Prerequisite: 513.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '525', 'Advanced Biomedical Imaging', '', 'Advanced scientific and engineering principles of biomedical imaging including magnetic resonance, X-ray computed tomography, ultrasound, and single photon and positron emission tomography. Prerequisites: BME 513 or EE 483. Open only to master and doctoral students.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '528', 'Medical Diagnostics, Therapeutics and Informatics Applicatio', '', 'Picture archive communication system (PACS) design and implementation; clinical PACS-based imaging informatics; telemedicine/teleradiology; image content indexing, image data mining; grid computing in large-scale imaging informatics; image-assisted diagnosis, surgery and therapy.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BME', '530', 'Introduction to Systems Biology', 'Stacey Finley', 2083949, 'Computational methods for simulation and analysis of metabolic networks, cellular signaling pathways, and gene regulatory networks. Biochemical kinetics, differential equations, stability analysis, sensitivity analysis.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '551', 'Introduction to Bio-MEMS and Nanotechnology', '', 'Principles and biomedical applications of micro-electromechanical systems (MEMS) and nanotechnology, including microfluidics, nanowire sensors, nanomotors, quantum dots, biofuel cells and molecular imaging. Recommended preparation: Basic Biology and Electronics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '552', 'Neural Implant Engineering', 'Ted Berger', 'Advanced studies of the basic neuroscience, engineering design requirements and technological issues associated with implantable neural prostheses, with paticular emphasis on retinal and cortical function.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '566b', 'Topics in Health, Technology and Engineering', '', 'Interdisciplinary approach to impart the skills, knowledge and familiarity with stages of collaborative projects related to medical device and methods innovation in healthcare settings. Open only to Health, Technology and Engineering majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BME', '650', 'Biomedical Measurement and Instrumentation', '', 'Design of measurement systems and biomedical instrumentation; architecture of electronic instruments used to measure physiological parameters; analysis of major process functions integrated in these instruments. Open to M.S., Medical Device and Diagnostic Engineering and biomedical engineering Ph.D students only. Recommended preparation: BME 513.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '120', 'Introduction to Chemical Engineering', 'Ted Lee', 1577035, 'Problem-solving techniques in chemical engineering, including an introduction to engineering problem analysis, mass balances, and energy balances.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '205', 'Numerical Methods in Chemical Engineering', 'Nicholas Graham', 'Computational tools for solving numerical problems in Chemical Engineering.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '330', 'Chemical Engineering Thermodynamics', 'Katherine Shing', 2044027, 'Elements of chemical engineering thermodynamics, including generalized correlations of properties of materials, phase behavior, physical and chemical equilibria.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '350', 'Introduction to Separation Processes', 'Noah Malmstadt', 1311122, 'Use of equilibrium phase relations and principles of material and energy balance for design, operation, and optimization of separation procedures such as distillation, absorption, etc. Recommended preparation: CHE 330', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '443', 'Viscous Flow', 'Pin Wang', 'Constitutive equations and rate laws, momentum equations and kinetic theory of Newtonian and complex flows. Applications to chemical engineering systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '444aL', 'Chemical Engineering Laboratory', 'Robert Young', 2257115, 'Resolution of chemical engineering problems which require original planning, observations, and data. Field trips to chemical plants. Written and oral reports.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '446', 'Mass Transfer in Chemical Engineering Processes', 'Ted Lee', 1577035, 'Molecular and continuum approaches to diffusion and convection in fluids and multicomponent mixtures; simultaneous mass, heat, and momentum transfer; steady-state and time-dependent diffusion; Maxwell-Stefan equations.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '460L', 'Chemical Process Dynamics and Control', 'Thomas Marlin', 'Simulation, stability and automatic control of chemical processes. Open and closed loop control schemes and introduction to optimal control theory. Computer implementation and laboratory application.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '475', 'Physical Properties of Polymers', 'Malancha Gupta', 2185189, 'Theoretical methods and semi-empirical correlations for estimating mechanical, thermodynamic, transport, optical, and electrical properties of polymer solutions, melts, networks, glasses, polymer blends and semi-crystalline polymers.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '476', 'Chemical Engineering Materials', 'Jongseung Yoon', 'Chemical and physical properties of solid materials used by chemical engineers, including polymers, metals, and ceramics. Materials design for industrial applications.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '480', 'Chemical Process and Plant Design', 'Robert Young', 2257115, 'Applications of unit operations, thermodynamics, kinetics, and economic balance; energy conservation in heat exchanger networks and in sequencing of separational devices. Safety aspects.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '489', 'Biochemical Engineering', 'Pin Wang', 'Application of chemical engineering principles to biological and biochemical processes and materials. Design of biochemical reactors and of processes for separation and purifications of biological products.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHE', '541', 'Mass Transfer', 'Muhammad Sahimi', 597282, 'Fundamentals of mass transfer within a single phase and between phases; applications to separation processes. Recommended preparation: 445', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '542', 'Chemical Engineering Kinetics', 'Theo Tsotsis', 'Reaction kinetics applied to problems of engineering design and operation. Recommended preparation: 442', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '544', 'Heat Transmission', 'Shaama Mallikarjun Sharada', 'Principles of conduction, radiation, and convection of heat; application to chemical and related industries. Recommended preparation: 430, 445', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '550b', 'Seminars in Chemical Engineering', '', 'Seminars to cover recent developments in the field of chemical engineering given by invited speakers. Master\'s students must register for 2 semesters; Ph.D. students must register for 4 semesters. Graded IP/CR/NC. Recommended preparation: graduate standing', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHE', '586', 'Process Data Analytics and Machine Learning', 'Joe Qin', 'Topics include multi-linear regression, supervised learning, unsupervised learning, principal component analysis, partial least squares, canonical correlation analysis, clustering methods, lasso, neural networks, and deep learning. Applications include analysis of chemical process data, quality data, and indirectly measured data.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '108', 'Introduction to Computer Methods in Civil Engineering', 'Carter Wellford', 'Computer programming, organization of problems for computational solution, flow charts, programming; numerical methods, analysis and solution of civil engineering problems.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '205', 'Statics', '', 'Statics of particles and rigid bodies; equivalent force systems; distributed forces; applications to trusses, frames, machines, beams, and cables; friction; moments of inertia.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '207', 'Introduction to Design of Structural Systems', '', 'Structural materials, components and systems; gravity and lateral forces; structural performance and failures; introduction to structural plans and analysis; computer applications, case studies, design project. Prerequisite: CE 205. Corequisite: CE 106L and CE 225.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '225', 'Mechanics of Deformable Bodies', '', 'Analysis of stress and strain; axial, flexural, and torsional behavior of slender bars; elastic deflections; combined stresses; introduction to elastic stability and energy methods.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '235', 'Dynamics', '', 'Elements of vector algebra; dynamics of particles, systems of particles and rigid bodies; kinematics; momentum relations, energy methods; vibrations; Euler\'s equations of motion. Prerequisite: CE 205. Duplicates credit in CE 325.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '402', 'Computer Methods in Engineering', 'Sami Masri', 'Fundamentals of analog and digital computers; simulation of nonlinear physical systems; numerical analysis and solution of engineering problems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '450', 'Basic Coastal Engineering', '', 'Mechanics of wave motion; wave refraction, diffraction and reflection; wave forecasting; shore processes; planning of coastal engineering projects; design of seawalls, breakwaters and offshore installations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '451', 'Water Resources Engineering', '', 'Discussion of broad perspectives on control and utilization of water, quantitative hydrology, ground water, probability concept, economic study, hydraulic structures, multi-purpose water resources projects.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '457', 'Reinforced Concrete Design', '', 'Strength and deformation of reinforced concrete; beams in flexure and shear; bond and development of bars; deflections; columns; slabs; footings; introduction to prestressed concrete.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '458', 'Theory of Structures II', 'Carter Wellford', 'Matrix algebra; stiffness method; force method; computer analysis of planar structures.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '460', 'Construction Engineering', '', 'Introduction to the construction processes; estimating and bidding, construction administration, planning and scheduling, equipment and methods, labor relations, cost control systems, and safety.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '462', 'Construction Methods and Equipment', '', 'Current procedures in selected fields of construction; organization and planning; equipment economics; machinery.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '463L', 'Water Chemistry and Analysis', '', 'Chemistry of water purification technology and water pollution control. Chemical processes in natural and engineering aquatic environments; physical/chemical and biological characterization of water and wastewater.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '465', 'Water Supply and Sewerage System Design', '', 'Design of water supply systems, storm drains, sanitary sewers, and lift stations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '466', 'Design of Free-Surface Hydraulic Systems', '', 'Hydrological and hydraulic design for uniform and non-uniform flows, channel transition, sedimentation controls, design discharge for tributary watersheds, flood routing, flood detention, computer aided design.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '467L', 'Geotechnical Engineering', '', 'Fundamentals of geotechnical engineering; soil classification, seepage, stress-strain behavior, shear strength, consolidation, design of retaining structures and foundations, and slope stability. Soil testing. Duplicates credit in CE 464 and CE 468.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '470', 'Building Information Modeling and Integrated Practice', 'Burcin Becerik', 'Building Information Modeling, current BIM technologies; coordination of design and construction; information management throughout building life cycle; project delivery systems and technologies for integrated practice.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '480', 'Structural Systems Design', '', 'Evaluate, design and analyze buildings. Organize and perform calculations for vertical loads, wind loads, and seismic loads on building projects.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '485', 'Wastewater Treatment Design', '', 'Process kinetics, mass balance, reactor design, pretreament, clarification, chemical treatment, biological treatment (aerobic and anaerobic), disinfection, sludge treatment, nitrogen and phosphorus removal, carbon adsorption.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '499', 'Special Topics', 'Burcin Becerik,David Gerber,Brad Cracchiola', 'Course content to be selected each semester from recent developments in civil engineering and related fields.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '501', 'Functions of the Constructor', '', 'Systems, processes, and constraints governing the initiation, direction, engineering, and delivery of major construction projects. Professional construction management, responsibilities, and practice.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '502', 'Construction Accounting and Finance', '', 'Cost control, finance, and engineering economy for construction operations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '503', 'Microbiology for Environmental Engineers', '', 'Basic microbiology of water, air, and soil. Application of microbiology to the practice of environmental pollution control.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '510', 'Groundwater Management', '', 'Groundwater hydrology, aquifer testing technology, groundwater quality and contamination, geophysical method, well design and development, basin water balance, computer modeling, legal aspects, groundwater management system.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '520a', 'Ocean and Coastal Engineering', '', 'Linear and nonlinear wave theories with engineering applications;wind waves; wave spectra; wave interactions with marine structures; ship mooring, harbor resonance; sediment transport; diffusion processes.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '529b', 'Finite Element Analysis', 'Carter Wellford', 'Basic concepts; stiffness method; variational methods; displacement method; isoparametric formulation; plane stress and strain; plates and shells; dynamics; stability; nonlinear analysis, heat transfer; computer applications.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '533', 'Geotechnical Earthquake Engineering', '', 'This course provides a design-oriented understanding of the "state-of-the-practice" of soil mechanics and foundation engineering aspects of earthquake engineering.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '539', 'Advanced Steel Structures', '', 'Design of tubular members and plate girders; design for torsional and seismic loads; general flexural theory; introduction to plastic design; connections.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '541b', 'Dynamics of Structures', 'Sami Masri', 'Continuous system response; approximate methods; introduction to structural control; random vibration concepts; responses of continuous systems to random excitation; nonlinear systems (geometric theory), (approximate methods).', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CE', '542', 'Theory of Plates', 'Abdel-Salam Niazy', 2268937, 'Theory of plate bending; rectangular and circular plates; anisotropic plates; energy methods; numerical methods; large deformations; sandwich plates.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '543', 'Stability of Structures', '', 'Critical loads of columns, beams, thin-wall bars, plates, shells; stability of frames and trusses; effect of inelastic behavior of materials; effect of dynamic loading.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '547', 'Response of Structures', '', 'Solutions of seismic structural response: vibrational vs. wave methods, spectral superposition, probabilistic response estimation, nonlinear response; soil-structure interaction; identification and structural health monitoring; experimental methods. Duplicates credit in former CE-535b.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '553', 'Biological Processes in Environmental Engineering', '', 'Fundamentals and design of aerobic and anaerobic biological treatment processes with a focus on removing organic and inorganic pollutants and recovering resources. Prerequisite: CE 453.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '556', 'Project Controls-Budgeting and Estimating', '', 'Fundamental principles and practices of cost estimating, budgeting, and cost control of construction projects. Case studies and software exercises based on project data.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '557', 'Advanced Building Estimating', '', 'Processes in compiling a bid for construction of non-residential building.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '561', 'Uncertainty Quantification', '', 'Methods of quantifying uncertainty in civil engineering and related fields. Basic uncertainty modeling; advanced topics such as reliability analysis, Bayesian updating, random processes, random fields.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '566', 'Project Controls - Planning and Scheduling', '', 'Fundamental principles and practices of planning, CPM scheduling, and resource management. Development of project schedules using CPM theory applied to current and emerging software applications.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '582', 'Transportation System Security and Emergency Management', '', 'Multimodal transportation resilience with emphasis on transit systems. Principles of emergency management, preparedness, vulnerability assessment, countermeasures. Related topics in policy and economics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '583', 'Design of Transportation Facilities', '', 'Planning, design, staging, construction, test, and maintenance of the public works and facilities for land, water, and air transportation. Recommended preparation: CE 471 and CE 457; Probability and statistics on the level of CE 408.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '599', 'Special Topics', '', 'Course content will be selected each semester to reflect current trends and developments in the field of civil engineering.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CE', '603', 'System Identification', 'Ketan Savla', 'Methods for building linear and nonlinear dynamical models from input and output time series data; theoretical models, algorithms, and toolbox implementation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '102L', 'Fundamentals of Computation', 'Mark Redekopp', 860888, 'Fundamental concepts of algorithmic thinking as a primer to programming. Introduction to C++.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '103L', 'Introduction to Programming', 'Andrew Goodney', 2163737, 'Basic datatypes, assignments, control statements (if, switch, for, while), input/output (printf, scanf, cin, cout), functions, arrays, structures, recursion, dynamic memory, file handling. Programming in C/C++.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '104L', 'Data Structures and Object Oriented Design', 'Sandra Batista', 2284407, 'Introduces the student to standard data structures (linear structures such as linked lists, (balanced) trees, priority queues, and hashtables), using the C++ programming language.Prerequisite: CSCI 103. Corequisite: CSCI 170.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '109', 'Introduction to Computer Science', 'Andrew Goodney', 2163737, 'An introduction to, and overview of, Computer Science; both as a discipline and a body of knowledge.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '170', 'Discrete Methods in Computer Science', 'Michael Shindler', 1853365, 'Sets, functions, series. Big-O notation and algorithm analysis. Propositional and first-order logic. Counting and discrete probability. Graphs and basic graph algorithms. Basic number theory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '201', 'Principles of Software Development', 'Jeffrey Miller', 1894993, 'Object-oriented paradigm for programming-in-the-large in Java; writing sophisticated concurrent applications with animation and graphic user interfaces; using professional tools on team project. Prerequisite: CSCI 104L.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '270', 'Introduction to Algorithms and Theory of Computing', 'Aaron Cote', 1634254, 'Algorithm analysis. Greedy algorithms, divide and conquer, dynamic programming, graph algorithms. NP-completeness and basic recursion theory and undecidability. Sorting lower bounds. Number-theory based cryptography.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '281', 'Pipelines for Games and Interactives', 'Scott Easley', 1307919, 'Explores the aesthetic development/technical implementation necessary to achieve unique, compelling, intuitive visual design in games. Students will develop group visual game design portfolios.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '310', 'Software Engineering', 'William Halfond', 1870652, 'Introduction to the software engineering process and software lifecycle. Covers project management, requirements, architecture, design, implementation, testing, and maintenance phase activities in team based projects. Prerequisites: CSCI 201. Duplicates credit in former CSCI 377.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '350', 'Introduction to Operating Systems', 'Gabriel Ghinita', 'Basic issues in concurrency, deadlock control, synchronization scheduling, memory management, protection and access control, inter-process communication, and structured design. Laboratory experiences with Unix-like operating system. Duplicates credit in CSCI 402.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '353', 'Introduction to Internetworking', 'William Cheng', 'Global Internet: design principles, layering, protocol design/analysis.Networked applications, Internet structure/architecture,Protocols for transport/congestion control, network layer/routing,link layer/MAC. Network security. Prerequisites: CSCI 201. Recommended preparation: Familiarity with C and C++.Duplicates credit in EE 450.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '356', 'Introduction to Computer Systems', 'Marco Paolieri', 'Computer organization; entity abstraction and representation; program execution; code optimization; memory usage; exception handling; processing control; computer performance; hands-on work done in C and assembly.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '360', 'Introduction to Artificial Intelligence', 'Chao Wang', 'Concepts and algorithms underlying the understanding and construction of intelligent systems. Agents, problem solving, search, representation, reasoning, planning, machine learning. Prerequisites: CSCI 104 and CSCI 170. Duplicates credit in former CSCI 460.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '401', 'Design and Construction of Large Software Systems', 'Jeffrey Miller', 1894993, 'Group project with an outside stakeholder to develop real-world software solutions to large-scale problems. Topics include software engineering, professional preparation, and recent computer science research. Duplicates credit in CSCI 477a and CSCI 477b.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '402', 'Operating Systems', 'William Cheng', 'Concurrency, deadlock control, synchronization, process and thread scheduling, memory management, file systems, security and access control, communication and networking, distributed file systems, data management.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '404', 'Creating Your High-Tech Startup', '', 'Capstone class in which students create their own technology startup, leveraging comprehensive CS knowledge and best industry practices. Prerequisite: CSCI 201, CSCI 270, and ITP 466.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '420', 'Computer Graphics', 'Jernej Barbic', 1792592, 'Computer graphics, OpenGL, 2D and 3D transformations, Bzier splines, computer animation, rendering including ray tracing, shading and lighting, artistic rendering, virtual reality, visualization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '423', 'Native Console Multiplayer Game Development', 'Artjoms Kovalovs', 'Implementation of AAA style multiplayer game running on consoles and DX11. Console development in native C++, console SDKs, engine components, gameplay, networking, data prediction/replication. Prerequisite: CSCI 522 or ITP 380. Recommended preparation: ITP 485.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '426', 'Game Prototyping', 'Andrew Nealen', 'Developing games or technology based on current and relevant special topics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '430', 'Introduction to Computer and Network Security', 'Jelena Mirkovic', 2017319, 'A broad overview of security threats and defenses, security systems and functionalities, as well as current security practices. Includes homeworks and in-class exercises to provide practical experience working with such systems. Prerequisite: CSCI 201.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '445L', 'Introduction to Robotics', 'Heather Culbertson', 'Designing, building and programming mobile robots; sensors, effectors, basic control theory, control architectures, some advanced topics, illustrations of state-of-the-art. Teamwork; final project tested in a robot contest. Junior standing or higher. Prerequisites: CSCI 103.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '455x', 'Introduction to Programming Systems Design', 'Claire Bono', 18521, 'Intensive introduction to programming principles, discrete mathematics for computing, software design and software engineering concepts. Not available for credit to computer sciencemajors, graduate or undergraduate.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '485', 'File and Database Management', 'Shahram Ghandeharizadeh', 336503, 'File input/output techniques, basic methods for file organization, file managers, principles of databases, conceptual data models, and query languages. Prerequisite: CSCI 201.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '491bL', 'Final Game Project', 'Scott Easley', 1307919, 'b: Design, iterative stage 2 prototyping and development of a refined game.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '499', 'Special Topics', 'Barath Raghavan', 'Selected topics in computer science.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '520', 'Computer Animation and Simulation', 'Jernej Barbic', 1792592, 'Fundamental techniques of computer animation and simulation, knowledge and/or experience in the design, scripting, production and post-production stages of computer animation. Prerequisite: CSCI-420 or CSCI-580. Recommendedpreparation: Familiarity with calculus, linear algebra, and numerical computation; C/C++ programming skills.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '526', 'Advanced Mobile Devices and Game Consoles', 'Mike Zyda', 'Explore the complex engineering process required to design and build a real-time graphics engine to support physical realism on mobile devices. Recommended preparation: CSCI 420 or CSCI 580 or an equivalent course in graphics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '529b', 'Advanced Game Projects', 'Mike Zyda,St Colon', 'Provides students in various areas of game specialization the practice of design, iterative stage 2 prototyping and development of a refined game.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '531', 'Applied Cryptography', 'Tanya Ryutov', 'Intensive overview of cryptography for practitioners, historical perspective on early systems, number theoretic foundations of modern day cryptosystems and basic cryptanalysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '538', 'Augmented, Virtual and Mixed Reality', 'Mike Zyda,Evangelos Lympouridis', 'Technical design and implementation of immersive environments; visual simulations, interactive 3D graphics and games.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '544', 'Applied Natural Language Processing', 'Ron Artstein', 2224374, 'Introduction to key components of human language technologies, including: information extraction, sentiment analysis, question answering, machine translation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '548', 'Information Integration on the Web', 'Jose-Luis Ambite,Shobeir Fakhraei', 'Foundations, techniques, and algorithms for information integration. Topics include Semantic Web, linked data, data integration, entity linkage, source modeling, and information extraction. Prerequisite: CSCI 561. Recommended preparation: CSCI 585 and some programming experience.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '551', 'Computer Networking', 'John Heidemann', 'Protocol design for computer communication networks, network routing, transport protocols, internetworking.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '561', 'Foundations of Artificial Intelligence', 'Ning Wang,David Pynadath', 'Foundations of symbolic intelligent systems, search, logic, knowledge representation, planning, learning.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '567', 'Machine Learning', 'Victor Adamchik', 2272065, 'Statistical methods for building intelligent and adaptive systems that improve performance from experiences; Focus on theoretical understanding of these methods and their computational implications. Recommended preparation: Undergraduate level training or coursework in linear algebra, multivariate calculus, basic probability and statistics; an undergraduate level course in Artificial Intelligence may be helpful but is not required.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '570', 'Analysis of Algorithms', 'Shahriar Shamsian', 1279784, 'Explores fundamental techniques such as recursion, Fourier transform ordering, dynamic programming for efficient algorithm construction. Examples include arithmetic, algebraic, graph, pattern matching, sorting, searching algorithms.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '571', 'Web Technologies', 'Marco Papa', 1104782, 'Advanced study of programming languages with application to the Web. Languages for client-side and server-side processing. Examples taken from: HTML, Java, JavaScript, Perl, XML and others. Recommended preparation: knowledge of at least two programming languages', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '572', 'Information Retrieval and Web Search Engines', 'Ellis Horowitz', 1263746, 'Examines key aspects of information retrieval as they apply to search engines; web crawling, indexing, querying and quality of results are studied.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '576', 'Multimedia Systems Design', 'Parag Havaldar', 500657, 'End-to-end multimedia systems - content creation, compression, distribution using modern standards, DRM solutions, Digital-Cinema pipeline, multimedia classification, virtual-augmented reality, natural-language multimedia queries, multimodal media analysis, stereoscopic-holographic technologies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '577b', 'Software Engineering', 'Supannika Koolmanojwong', 1868343, 'b: Software development, test, implemetation, and maintenance methods. CASE tools and software envirnments. Software product engineering, configuration management, quaility engineering, documentation. Application via projects.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '585', 'Database Systems', 'Sathyanaraya Raghavachary', 'Database system architecture; conceptual database models; semantic, object-oriented, logic-based, and relational databases; user and program interfaces; database system implementation; integrity, security, concurrency and recovery. Recommended preparation: Knowledge of relational databases, SQL, relational algebra and physical database design is required. Open only to graduate students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CSCI', '591', 'Computer Science Research Colloquium', 'Lizsl De Leon,Katie Williams', 'Exploration and critical assessment of research activities in computer science. Course will serve as a forum for current research presentations from academia and industry. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '599', 'Special Topics', 'Joseph Lim', 2286743, 'Course content to be selected each semester from recent developments in computer science.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '621', 'Digital Geometry Processing', 'Hao Li', 2310926, 'Digital geometry processing (subfield of computer graphics), that covers the full pipeline from 3D scanning, processing, to 3D printing. Recommended preparation: Solid background in linear algebra, numerical optimization, and C/C++ programming. CSCI 420 Computer Graphics is recommended. Open only to Computer Science doctoral students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '670', 'Advanced Analysis of Algorithms', 'Ming-Deh Huang', 203714, 'Fundamental techniques for design and analysis of algorithms. Dynamic programming; network flows; theory of NP-completeness; linear programming; approximation, randomized, and online algorithms; basic cryptography. Prerequisite: CSCI 570. Recommended preparation: familiarity with algorithms and discrete mathematics. Open only to doctoral students.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '685', 'Advanced Topics in Database Systems', 'Shahram Ghandeharizadeh', 336503, 'Advanced techniques in database management. Topics include optimization, cache management, data mining and knowledge discovery, decision support, spatial indexes, parallel and distributed systems, extendible storage. Prerequisite: CSCI 485 or CSCI 585.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '697', 'Seminar in Computer Science Research', 'Leana Golubchik', 1184961, 'Introduction of Ph.D. students to the broad range of computer science research. Two semesters registration required. Open to Computer Science doctoral students only. Graded CR/NC. Duplicates credit in former CSCI 597.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '698', 'Practicum in Teaching Computer Science', 'Laurent Itti', 1943288, 'Practical principles for the long-term development of effective teaching in Computer Science. Intended for teaching assistants for classes offered by the Computer Science department. Graded CR/NC. Open only to Computer Science doctoral students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CSCI', '699', 'Special Topics', 'Paul Rosenbloom', 1792111, 'Course content to be selected each semester from recent developments in computer science.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '109L', 'Introduction to Embedded Systems', '', 'Information representations, embedded programming, digital and serial I/O, analog-to-digital conversion, and interrupt mechanisms. Elementary analog, logic, and state-machine design.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '202L', 'Linear Circuits', 'Edward Maby', 22292, 'Lumped circuit elements; network equations; zero-input and zero-state responses; sinusoidal steady-state analysis; impedance;resonance; network functions; power concepts; transformers; Laplace transforms.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '250L', 'Distributed Systems for the Internet of Things', 'Shahin Nazarian', 1280034, 'Introduction to hardware, operating systems, signal processing and control, network protocols, mobile applications, databases, cloud computing, machine learning, and security for the Internet of Things.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '301', 'Linear Systems', 'Fariba Ariaei', 2360647, 'Representation and analysis of linear time-invariant systems primarily for the continuous time case. Convolution, Fourier series and transform, Laplace transform, controls and communications applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '322', 'Introduction to Digital Audio', 'Chris Kyriakakis', 1927142, 'Fundamentals of sound, acoustics and digital audio signal processing.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '326Lx', 'Essentials of Electrical Engineering', 'Douglas Burke', 1564049, 'Network analysis and theorems; transient analysis; transformers; semiconductor physics and circuits; power amplifiers, modulation and demodulation, and pulse, digital, and switching circuits. Introduction to instrumentation. Not available for credit to electrical engineering majors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '330', 'Electromagnetics I', 'Gianluca Lazzi', 'Basic static and dynamic electromagnetic field theory and applications; electrostatics, magnetostatics, Maxwell\'s equations, energy flow, plane waves incident on planar boundaries, transmission lines.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '338', 'Physical Electronics', 'Stephen Cronin', 1062145, 'Semiconductor device characteristics and applications. Physical models of electronic conduction in solids, p-n junctions, bipolar and field effect transistors and other solid-state devices.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '348L', 'Electronic Circuits', 'Dina El-Damak', 'Basic analog and digital circuit design using Bipolar Junction Transistors, Field Effect Transistors, and integrated circuits.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '354L', 'Introduction to Digital Circuits', 'Gandhi Puvvada', 15633, 'Digital system design and implementation; synchronous design of datapath and control; schematic/Verilog-based design, simulation, and implementation in Field Programmable Gate Arrays; timing analysis; semester-end project. Prerequisite: EE 101 or EE 154L. (Duplicates credit in EE 254 and in former EE 201L)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '355x', 'Software Design for Electrical Engineers', 'Shahin Nazarian', 1280034, 'Object-oriented programming techniques, basic data structures, and elementary complexity analysis for the modeling, simulation, and solution of engineering problems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '364', 'Introduction to Probability and Statistics for Electrical Engineering and Computer Science', 'Rahul Jain', 1832784, 'Introduction to concepts of randomness and uncertainty: probability, random variables, statistics. Applications to digital communications, signal processing, automatic control, computer engineering and computer science.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '434Lx', 'Digital Signal Processing Design Laboratory', 'Panayiotis Georgiou', 'Experiments and design project in digital signal processing (e.g. real-time DSP, acoustics, video) including: system specification, preliminary analysis, trade-off studies, implementation, presentation. Recommended preparation: EE 469. Capstone design experience.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '438L', 'Processing for Microelectronics', 'Kian Kaviani', 131370, 'Applications and electrical evaluation of selected processes used in electronic microfabrication.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '443', 'Introduction to Power Systems', 'Mohammed Beshir', 1545553, 'Components of power systems; analysis techniques in electrical power generation transmission and utilization; and environmental and economic considerations in system operations and planning.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '448L', 'Communication Electronics', 'Hossein Hashemi', 1926615, 'Analysis, design, and experimental evaluation of transistor-level communication circuits and micro-systems. Transmission lines, impedance matching, noise, distortion, tuned amplifiers, mixers, oscillators, phase-locked loops.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '450', 'Introduction to Computer Networks', '', 'Network architectures; layered protocols, network service interface; local networks; long-haul networks; internal protocols; link protocols; addressing; routing; flow control; higher level protocols.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '451', 'Parallel and Distributed Computation', 'Xuehai Qian', 2329794, 'Introduction to parallel programming techniques, models and optimization strategies; Application mapping to multi-core, accelerator, GPU and cloud platforms; High Performance Computing and Data Science applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '457', 'Computer Systems Organization', 'Gandhi Puvvada', 15633, 'Register Transfer level machine organization; performance; arithmetic; pipelined processors; exceptions, out-of-order and speculative execution, cache, virtual memory, multi-core multi-threaded processors, cache coherence.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '459', 'Embedded Systems Design Laboratory', 'Allan Weber', 1983697, 'Specification, design, implementation, testing and documentation of a digital system project using embedded processors, programmable logic, analog I/O interfaces and application specific hardware. Capstone design experience.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '467', 'Introduction to Communication Systems', 'Alan Willner', 1838953, 'Analog and digital communication systems. Modulation (AM, FM) Analog and digital communication systems. (De)modulation and (de)multiplexing of AM/FM/PM, noise, digital data formats, error rates, and spectral analysis. Review of wireless, networking, and optical systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '477L', 'MOS VLSI Circuit Design', 'Massoud Pedram', 458811, 'Analysis and design of digital MOS VLSI circuits including area, delay and power minimization. Laboratory assignments including design, layout, extraction, simulation and automatic synthesis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '483', 'Introduction to Digital Signal Processing', '', 'Fundamentals of Digital Signal Processing covering: discrete timelinear systems, quantization, sampling, Z-transforms, Fouriertransforms, FFTs and filter design.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '484', 'Communication System Design', '', 'Design and analysis of analog and digital communication systems. System models, requirements, development, performance analysis and component selection techniques. Comprehensive system design project. Capstone design experience. Open only to seniors. Not available for graduate credit. Prerequisite: EE 364, EE 475. Recommended preparation: EE 467', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '503', 'Probability for Electrical and Computer Engineers', 'Vijay Kumar', 'Rigorous coverage of probability, discrete and continuous random variables, functions of multiple random variables, covariance, correlation, random sequences, Markov chains, estimation, and introduction to statistics. Duplicates credit in former EE 464 and EE 465.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '504L', 'Solid State Processing and Integrated Circuits Laboratory', 'Kian Kaviani', 131370, 'Laboratory oriented with lectures keyed to practical procedures and processes. Solid-state fabrication and analysis fundamentals; basic device construction techniques. Prerequisite: BSEE', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '507', 'Micro- and Nano-Fabrication Technology', 'Wei Wu', 'Physical basis of technologies for the fabrication of micro- and nano-scale devices. Thin-film deposition, etching, and material modification processes; patterntransfer methods.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '510', 'Linear Algebra for Engineering', '', 'Introduction to linear algebra and matrix theory and their underlying concepts; applications to engineering problems; mathematically rigorous and foundational to other classes in communication, control, and signal processing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '511', 'Simulation Methods for Stochastic Systems', 'Osonde Osoba', 2338530, 'Project-oriented investigation of simulation methods used for the analysis and design of complex stochastic systems whose operation and performance are affected by random events. Corequisite: EE 503. Recommended preparation: MatLab programming experience.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '512', 'Stochastic Processes', 'Ashutosh Nayyar', 2090012, 'Probability theory and stochastic processes, including renewal theory, Markov chains, Brownian motion, martingales, and stochastic calculus. Applications in communication networks, queuing theory, and financial systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '517', 'Statistics and Data Analysis for Engineers', '', 'Statistics and data analysis emphasizing computation and problem solving: confidence intervals, hypothesis tests, bootstrap and Monte Carlo estimation, regression, Bayesian and statistical learning techniques.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '518', 'Mathematics and Tools for Financial Engineering', 'Petros Ioannou', 1581152, 'Students will build a mathematical background for studying Financial Engineering. Emphasis is on analysis, proofs and examples. Mathworks Financial toolbox will be introduced.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '519', 'Speech Recognition and Processing for Multimedia', 'Shrikanth Narayanan', 'Speech production, acoustics, perception, synthesis, compression, recognition, transmission. Coding for speech, music, and CD-quality. Feature extraction. Echo cancellation. Audio, visual synchronization. Multimedia, Internet use.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '521', 'Power Systems Analysis and Design', 'Mohammed Beshir', 1545553, 'Power system planning, studies, and design; time-domain modeling and analysis of power-system networks; power flow, stability, fault, and economic dispatch analysis; symmetrical components.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '522', 'Immersive Audio Signal Processing', 'Chris Kyriakakis', 1927142, 'Fundamentals of digital audio signal processing, room acoustics, and psychoacoustics. Algorithms for real-time implementation of immersive audio systems for integrated media applications.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '524', 'Transients in Power Systems', 'Syed Ahmed', 'Overvoltages during faults, voltage recovery, arcing faults, restrikes, theory of switching surges. Systems grounding, traveling waves, lightning and surge protection, insulation coordination.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '526', 'Renewable Energy in Power Systems', 'Mohammed Beshir', 1545553, 'Renewable energy sources and their integration in electrical networks. Power-flow control from highly variable resources. Cost analysis and planning.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '527', 'Net-Centric Power-System Control', 'Edmond Jonckheere', 764118, 'Control and stability of large-scale systems such as the electric power grid. Integration with information networks. Corequisites: EE 521. Recommended preparation: EE 482.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '533', 'Network Processor Design and Programming', 'Young Cho', 'Understanding of network processor architecture, applications, and other relevant issues. Program network processor and test under realistic network environment. Design and deploy custom network processor. Prerequisite: EE 457. Recommended preparation: EE 450.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '535', 'Mobile Communications', 'Andreas Molisch', 2059426, 'The mobile communication channel; techniques used to combat the channel; cellular communications; multiple-access techniques; example mobile communication systems. Prerequisite: EE 503.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '536b', 'Mixed-Signal Integrated Circuit Design', 'Shuo-Wei Chen', 'Non-linear integrated circuits, data-converter architectures and implementations, comprehensive design project.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '540', 'Introduction to Quantum Electronics', 'Martin Gundersen', 916147, 'Fundamentals of light amplification; laser amplifiers and oscillators; atomic pumping; maser and laser systems; definitions of coherence; measurements in quantum electronics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '543a', 'Digital Control Systems', 'Richard Chiang', 'Design, analysis and implementation of digital control systems using microcomputers; Z-transform methods; frequency domain and state space approach; computational aspects; sampling and quantization.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '544', 'Radio Frequency Systems and Hardware', 'Ali Zahid', 653973, 'Elements of radio frequency communication systems: modulation/demodulation strategies, transmission-channel impairments, performance criteria, hardware (low-noise amplifiers, mixers, oscillators), digital back-end, contemporary case studies.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '552', 'Asynchronous VLSI Design', 'Dan Gunnar Mika Nystroem', 'Asynchronous channels and architectures; implementation design styles; controller synthesis; hazards, and races; Petri-nets; performance analysis, and optimization; globally asynchronous locally synchronous design. Open only to graduate students.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '555', 'Broadband Network Architectures', 'Ali Zahid', 653973, 'Broadband network architectures and services, technologies for high-speed access and core networks, optical infrastructure for layered network architectures, high performance switch and router architectures. Prerequisites: EE 450 and EE-503.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '557', 'Computer Systems Architecture', 'Michel Dubois', 'Computer architecture from a design perspective: Pipelined processors, speculative execution, VLIW, vector processors, GPU/GPGPU, memory technology and systems, interconnection networks, shared-memory and message-passing multiprocessors, chip multiprocessors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '559', 'Mathematical Pattern Recognition', 'Keith Jenkins', 1243201, 'Distribution free classification, discriminant functions, training algorithms; statistical classification, parametric and nonparametric techniques; artificial neural networks.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '562', 'Random Processes in Engineering', 'Bob Scholtz', 'Random vectors, sequences, and functions. Linear transformations, second moment theory, spectral densities, narrowband processes, Gaussian processes, correlation detection, linear minimum mean square error estimation.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '563', 'Estimation Theory', 'Maryam Shanechi', 2313359, 'Parameter estimation and state estimation technique including: least squares, BLUE, maximum-likelihood, maximum a posteriori, Kalman-prediction, Kalman-filtering and Kalman smoothing and extended Kalman filtering.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '564', 'Digital Communication and Coding Systems', 'Christopher Walker', 'Digital modulations. Optimal reception. Performance analysis. Classical and modern codes. Viterbi, forward-backward, and iterative decoding. Practical designs for channels with memory or nonlinearities. Example systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '565', 'Information Theory and Compression', 'Urbashi Mitra', 490858, 'Entropy and mutual information. Variable and fixed-length, lossless and lossy compression. Universal compression. Text and multimedia compression. Channel capacity. Error-correcting codes. Erasure and Gaussian channels. Prerequisite: EE 503. Duplicates credit in former EE 565a.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '566', 'Optical Information Processing', 'Keith Jenkins', 1243201, 'Coherent and incoherent optical transforming, imaging and two-dimensional information processing systems; optical image processing, spatial frequency response and filtering; optical and digital holography.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '569', 'Introduction to Digital Image Processing', 'Jay Kuo', 414206, 'Image sampling, 2-D image transform, image enhancement, geometric image modification, morphologic processing, edge detection, texture analysis, image filtering and restoration.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '570a', 'Advanced Electromagnetic Theory', 'Aluizio Prata', 1919497, 'Classical electromagnetic field theory and its usage in understanding and analytically modelling advanced applied electrical engineering devices.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '571a', 'Microwave Networks', '', 'a: Microwave network theory for transmission lines and waveguides, discontinuities, impedance transformers, resonators, multi-junction networks, periodic structures, non-reciprocal and active devices. Prerequisite: 470. b: Parameter matrices, approximate design procedures for distributed networks from lumped networks, coupled lines, equivalent coupled-line circuits, Kuroda\'s identities, and capacitance matrix transformations. Prerequisite: 470.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '576', 'Integrated Memory Devices and Technologies', 'Han Wang', 'Physical operation and implementation of modern solid-state memory structures. Emerging advanced memory technologies.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '577a', 'VLSI System Design', 'Pierluigi Nuzzo', 'Integrated circuit fabrication; circuit simulation; basic device physics; simple device layout: structured chip design; timing; project chip; MOS logic; system design; silicon compilers.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '577b', 'VLSI System Design', 'Mohammad Mirza Aghatabar Ahangar', 'VLSI design project; chip level design issues: power and clock distribution, packaging, I/O; design techniques; testability; chip fabrication and test.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '582', 'CMOS:Nano Neuromorphic Circuits', 'Alice Parker', 18547, 'Design and simulation of CMOS and nano electronic circuits modeling brain cells, including neurons and glial cells; low-power design; simulation laboratory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '586L', 'Advanced DSP Design Laboratory', 'Panayiotis Georgiou', 'Real-time adaptive signal processing design projects using special purpose DSP processors. Suitable project areas include acoustics, speech, arrays, image compression and biomedical signal processing. Permission of instructor required.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '591', 'Magnetic Resonance Imaging and Reconstruction', 'Krishna Nayak', 'Principles of magnetic resonance imaging. Spin physics, Fourier-based acquisition and reconstruction, generation of tissue contrast, fast imaging, artifact correction, advanced image reconstruction. Prerequisite: EE 483. Recommended preparation: EE-441 and EE-503. Familiarity with MATLAB is required.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '596', 'Wavelets and Graphs for Signal Processing and Machine Learni', 'Antonio Ortega', 1741359, 'Multirate signal processing, wavelets and filter banks. Overcomplete and sparse signal representations. Graph signal processing. Applications to compression, sensing, time-frequency analysis, and machine learning.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '597', 'Wireless Networks', 'Bhaskar Krishnamachari', 131929, 'Introduction to wireless networking technologies; fundamental architectural and design principles used at all protocol layers; optimization and performance evaluation using mathematical analysis and simulations. Prerequisites: EE-450 and EE-503. Recommended preparation: EE 467, familiarity with Matlab and C programming.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '598', 'Electrical Engineering Research Seminar', 'Paul Bogdan', 'Introduction to research in electrical engineering. Topics vary by semester. May be repeated for up to one unit of credit for MS students, two units of credit for PhD students. Open only to Masters and Doctoral Students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EE', '599', 'Special Topics', 'Paul Dapkus', 'The course content will be selected each semester to reflect current trends and developments in the field of electrical engineering.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '601', 'Advanced Semiconductor Device Physics', 'Tony Levi', 1927138, 'Fundamentals and limitations of nanoscale electronic and photonic semiconductor components. Engineering classical, mesoscale, and quantum electron and photon dynamics in advanced devices.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '637L', 'Current and Future Computing Devices and Technology', 'Rehan Kapadia', 2182956, 'State-of-the-art computing devices; current trends in device technologies; future directions.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EE', '642', 'Advanced Geometrical Optics', 'Armand Tanguay', 1254706, 'First order design of optical systems; origin of aberrations and their effects on wave propagation and imaging based on geometrical and physical optics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '200', 'Environmental Engineering Principles', '', 'Analysis of water, air, and land pollution, including hazardous waste, and engineering of mitigation measures. Water and wastewater treatment systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '426', 'Properties/Behavior/Measurement', '', 'Particulate air pollutants, their measurement and instrumentation methods, and their effects on the environment and human health; optical properties and visibility degradation. Prerequisite: ENE 200. (Duplicates credit in former ENE 526)', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '428', 'Air Pollution Fundamentals', '', 'Air pollution effects on man, vegetation, materials; pollutant sampling and analysis; air quality standards and criteria; meteorological factors and dispersion modeling. Prerequisite: MATH-245 and PHYS-151 and CHEM-105b or CHEM-115b. Recommended preparation: ENE 200 or CHE 350.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '502', 'Environmental and Regulatory Compliance', '', 'Federal and state environmental laws; environmental impact assessment techniques; permitting for industrial facility construction and operation. Graduate standing.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '510', 'Water Quality Management and Practice', '', 'Surface and ground water quality and resources management; water pollution in aquatic environment; water/wastewater infrastructure systems and management. Departmental approval.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '512', 'Environmental Fluid Mechanics', '', 'Environmental fluid mechanics (air and surface/ground water); flow kinematics; viscous/inviscid flow; boundary layer theory; steady/unsteady groundwater flows; turbulence; Reynolds equations; contaminant transport.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '535', 'Exposure, Health Effects and Risk', '', 'Pollutant sampling; occupational, community, and personal exposures; receptor modeling; data analysis; health effects of air pollutants. Departmental approval.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENE', '562', 'Aquatic Chemistry', '', 'Quantitative prediction of chemical speciation in aquatic systems, including acid-base chemistry, precipitation/dissolution, complexation, and oxidation/reduction. Applications to water treatment.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '100a', 'Engineering Honors Colloquium', '', 'Recent developments in a highly technological society with emphasis on selected topics. Enrollment limited to members of the School of Engineering Honors Program. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '100b', 'Engineering Honors Colloquium', '', 'Recent developments in a highly technological society with emphasis on selected topics. Enrollment limited to members of the School of Engineering Honors Program. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '100c', 'Engineering Honors Colloquium', '', 'Recent developments in a highly technological society with emphasis on selected topics. Enrollment limited to members of the School of Engineering Honors Program. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '100d', 'Engineering Honors Colloquium', '', 'Recent developments in a highly technological society with emphasis on selected topics. Enrollment limited to members of the School of Engineering Honors Program. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ENGR', '395dx', 'Cooperative Education Work Experience', 'Louise Yates', 143452, 'Supervised work experience in a professional environment related to specific degree program, academic level, and career objective. Acceptance into Cooperative Education Program is required. Graded IP or CR/NC. Degree credit by departmental approval.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '401x', 'Communicating Science and Engineering to Children', '', 'Engineering students communicate their knowledge, collaborate constructively with peers, and inspire underserved children to develop a curiosity and persistence for science and engineering. Open only to Junior and Senior Engineering students.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ENGR', '410', 'Social Media for Scientists and Engineers', 'Sarah Mojarad', 2300986, 'An introduction to the various ways in which social media and science are intersecting in a compelling manner; new technologies and online communities are powerful tools that are changing the ways in which scientists are engaging one another (inreach) and general audiences (outreach).', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '502x', 'Writing Skills for Engineering Ph.D. Students', 'Elizabeth Fife', 'Writing of engineering curriculum- and research-related projects for Ph.D. students. Focus is on conference papers, dissertations and proposals, journal articles, and other forms. Graded CR/NC. Not available for credit to master\'s students.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ENGR', '503x', 'Oral Communication Skills for Engineering Ph.D. Students', 'Elizabeth Fife', 'Academic and professional presentation skills for Ph.D. students. Preparation for qualifying exams, conference paper presentations, and other forms of oral communication. Use of visual aids and poster displays included. Graded CR/NC. Not available for degree credit to master\'s students.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '150', 'Solving Engineering Problems via Computer Programming', 'Nathan Greenfield', 2068193, 'Engineering examples are used as a platform to introduce computer programming skills for problem solving; critical thinking skills.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '220', 'Probability Concepts in Engineering', 'Cesar Acosta-Mejia', 'Techniques for handling uncertainties in engineering design: discrete and continuous random variables; expectations, probability distributions and transformations of random variables; limit theorems; approximations and applications. Prerequisite: MATH 126.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '225', 'Engineering Statistics I', 'Cesar Acosta-Mejia', 'Sampling distributions; parameter estimation, hypothesis testing; analysis of variance; regression; nonparametric statistics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '315L', 'Engineering Project Management', 'Yalda Khashe', 'Techniques for planning, budgeting, scheduling, obtaining resources, monitoring and control of projects in a corporate environment. Use of software to evaluate performance and assess risks. Prerequisite: ISE 225 and ISE 330.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '327', 'Six Sigma and Lean Operations', 'Yalda Khashe', 'Standardized methodology for problem solving, basic and advanced statistical tools for root cause identification and solution confirmation, key principles for quality management and organizational excellence. Prerequisite: ISE 225.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '330', 'Deterministic Models', 'Phebe Vayanos', 'Introduction to linear programming; transportation and assignment problems; dynamic programming; integer programming; nonliner programming.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '331', 'Stochastic Models', 'John Carlsson', 2090286, 'Stochastic processes; Markov chains; queueing theory and queueing decision models; probabilistic inventory models. Recommended preparation: ISE 330', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '335L', 'Supply Chain Design', 'Geza Bottlik', 1541116, 'Design of Supply Chains. Product, distribution, transportation and site selection. Analysis and optimization of supply chain networks.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '350', 'Principles of Systems Engineering', 'Neil Siegel', 'Systems as complex collaborative ensembles of interconnected components. Theory and practice of requirements, design, implementation, testing, deployment, operation, and disposal. Case studies from real projects.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '370L', 'Human Factors in Work Design', 'Yalda Khashe', 'Physiological systems and psychological characteristics; ergonomics; anthropometry; effects of the physical environment on humans; occupational safety and health; work methods.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '375L', 'Facilities Design', 'Shalini Gupta', 'Design of facilities for operations and distribution. Product, process flow, material handling, and facility location techniques that lead to making good decisions for facilities layouts.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '382', 'Concepts, Design and Implementation', 'Nitin Kale', 209166, 'Data models for industry applications. Modeling and designing robust databases. Implementing and querying databases with SQL. Innovations in database applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '410', 'Planning and Scheduling', 'Geza Bottlik', 1541116, 'Production planning, forecasting, scheduling, and inventory; computer integrated decision systems in analysis and control of production systems. Prerequisite: ISE 330.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '426', 'Statistical Quality Control', 'Qiang Huang', 'Quantitative aspects of statistical quality control (process control, acceptance sampling by attribute and by variable, rectifying inspection), quality assurance and the management of QC/QA functions.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '435', 'Discrete Systems Simulation', 'Kurt Palmer', 1255724, 'Model design to simulate discrete event systems with basic input and output analysis using high order languages, applied to industrial systems analysis and design problems. Prerequisites: CSCI 101 and ISE 225.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '440', 'Work, Technology, and Organization', 'Mansour Rahimi', 'Impact of technology on work and organizational design; effects of automation; design of improvement programs; information infrastructures; teams; individual behavioral outcomes. Upper division standing.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '460', 'Engineering Economy', 'Erich Kreidler', 1362474, 'Utilizing principles of economic analysis for choice of engineering alternatives and engineering systems. Pre-tax and after-tax economy studies. Upper division standing.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '495ax', 'Senior Design Project', '', 'a: Preparation and development of the senior project proposal. Not available for graduate credit. Senior standing in industrial and systems engineering. Prerequisites: ISE 225 and ISE 460 and DSO 435 or ISE 382.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '495bx', 'Senior Design Project', 'Theodore Mayeshiba', 'Group work on an industrial engineering design problem in an organization. Not available for graduate credit. Senior standing in industrial and systems engineering. Prerequisites: ISE 315 and ISE 435 and ISE 370 or ISE 470. Open only to Industrial and Systems Engineering majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '500', 'Statistics for Engineering Managers', 'Geza Bottlik', 1541116, 'Framing engineering management situations with statistical methods. Experiments, distributions, regression, ANOVA, hypothesis.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '505', 'Modeling for Health Policy and Medical Decision Making', 'Sze-Chuan Suen', 'Modeling techniques used to inform decisions in public health, disease control, and hospital operations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '506', 'Lean Operations', 'Theodore Mayeshiba', 'Study of lean principles and practices as applied to various industries.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '509', 'Practicum in Health Care Systems', 'David Belson', 'Performance improvement projects within hospitals are undertaken by small teams collaborating with hospital staff. Study of tools to improve and optimize operations.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '510', 'Advanced Computational Design and Manufacturing', 'Yong Chen', 1547231, 'Study advanced concepts behind computational representations, algorithms, and mathematical foundations, and their applications in computer-aided design and manufacturing. Develop hands-on computational skills in team projects. Recommended preparation: bachelor\'s degree in industrial engineering; programming experience, C++ preferred.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '513', 'Inventory Systems', 'Geza Bottlik', 1541116, 'Deterministic and stochastic demand systems with static/dynamic models. Practice in inventory management, computerized procedures, materials requirements planning, just-in-time production, Kanban systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '514', 'Advanced Production Planning and Scheduling', 'Shalini Gupta', 'Advanced concepts in production planning and scheduling including resource allocation, lot sizing, flow shop and job shop scheduling, workforce scheduling and assembly line balancing. Recommended preparation: prior knowledge of operations research and probability theory.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '515', 'Engineering Project Management', 'Ali Nowroozi', 1528593, 'Applying industrial and systems engineering skills to problems drawn from industry, while working in teams of 3-4 students. Teach project management skills and provide direct experience in managing and executing a group project.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '525', 'Design of Experiments', 'Kurt Palmer', 1255724, 'Planning data collection to investigate relationships between product/process design choices (materials, temperatures, etc.) and performance, empirical modeling to predict performance, identification of the best design choices. Recommended preparation: ISE 225', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '527', 'Quality Management for Engineers', 'Thomas Booth', 'Principles of quality management, quality philosophies and frameworks, quality leadership and strategic planning, process management, and performance measurements.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '533', 'Integrative Analytics', 'Suvrajeet Sen', 1892488, 'The three pillars of analytics (predictive, prescriptive, and validation) together under one framework. Use of statistics, learning enabled optimization. Based on publicly available data sets.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '536', 'Linear Programming and Extensions', 'Sima Parisay', 1988590, 'Linear programming models for resource allocation; simplex and revised simplex methods; duality; sensitivity; transportation problems; selected extensions to large scale, multiobjective, and special structured models.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '539', 'Stochastic Elements of Simulation', '', 'Simulation techniques combined with probabilistic analysis for solving problems in inventory theory, queuing theory, financial engineering, decision analysis, and other fields having a stochastic element.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '544', 'Management of Engineering Teams', 'Ali Nowroozi', 1528593, 'Design and management of engineering teams. Group decision-making, motivation, leadership, infrastructure requirements, performance measurement, team diversity, conflict, and integration.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '561', 'Economic Analysis of Engineering Projects', 'Shalini Gupta', 'Economic evaluations of engineering systems for both government and private industry; quantitative techniques for evaluating non-monetary consequences; formal treatment of risk and uncertainty. Prerequisite: ISE 500. Recommended preparation: An undergraduate course in engineering economy.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '562', 'Value and Decision Theory', 'Detlof Von Winterfeldt', 'Decision making under risk conditions; utility theory; sufficient statistics; conjugate prior distributions; terminal and pre-posterior analysis; Bayesian statistics versus classical statistics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '563', 'Financial Engineering', 'Cesar Acosta-Mejia', 'Concepts underlying the economic analysis of engineering projects; applications to call and put options; utility theory and mathematical optimizations models; and simulation. Recommended preparation ISE 220 or an equivalent course in probability.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '576', 'Technology-Environment Interaction', 'Robert Vos', 2082054, 'Concepts and methods to analyze the environmental impacts of industrial systems, including life-cycle assessment, material flow analysis, design for environment and sustainable consumption.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '580', 'Performance Modeling with Simulation', 'Sima Parisay', 1988590, 'Introduction to modeling and analysis of stochastic systems, with an emphasis on discrete-event simulation of non-Markovian systems.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '583', 'Enterprise Wide Information Systems', 'Richard Vawter', 1853919, 'The role of enterprise resource planning systems (ERPs) in an organization and the task of implementing and managing the IS function.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '599', 'Special Topics', 'Saeed Mohasseb', 'Course content will be selected each semester to reflect current trends and developments in the field of industrial and systems engineering.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '620', 'Foundations of Stochastic Processes', 'Sheldon Ross', 2131532, 'An introductory PhD course on stochastic processes, covering such topics as conditional expectation, renewal processes, and discrete and continuous time Markov chains.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '630', 'Foundations of Optimization', 'Jong-Shi Pang', 'Convex sets, convex functions, structures of optimization problems, Lagrangian and conjugate duality. First and second order optimality conditions; applications in engineering and management. Recommended preparation: Calculus III and Linear Algebra.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ISE', '632', 'Network Flows and Combinatorial Optimization', 'John Carlsson', 2090286, 'Combinatorial optimization, particularly graph problems. Shortest paths, max flow, minimum cost flows, Spanning trees, matroids, submodular functions. Bipartite and general matchings, Polyhedral combinatorics, total unimodularity. Prerequisite: ISE 536. Recommended preparation: Familiarity with the theory of linear programming and with mathematical proofs; knowledge of linear algebra. Duplicates credit in ISE 532.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ISE', '651', 'Seminar in Industrial & Systems Engineering', 'Yigal Arens', 'Current research, guest speakers in the field; review papers; guidance in preparing research proposals and special projects. Duplicates credit in the former ISE-650abc. Open only to Fifth Year Seniors and Masters Students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '352', 'Applied Machine Learning and Data Mining', 'Mohammad Reza Rajati', 'Foundational course focusing on the understanding, application, and evaluation of machine learning and data mining approaches in data intensive scenarios.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '510', 'Principles of Programming for Informatics', 'Yigal Arens', 'Programming in Python for retrieving, searching, and analyzing data from the Web. Programming in Java. Learning to manipulate large data sets.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '525', 'Trusted System Design, Analysis and Development', 'Tanya Ryutov', 'Analysis of computer security and why systems are not secure. Concepts and techniques applicable to the design of hardware and software for Trusted Systems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '526', 'Secure Systems Administration', 'Gabriel Ghinita', 'The administrator\'s role in information system testing, certification, accreditation, operation and defense from cyber attacks. Security assessment. Examination of system vulnerabilities. Policy development. Recommended preparation: Previous degree in computer science, mathematics, computer engineering, informatics, and/or information security undergraduate program. Also, it is highly recommended that students have successfully completed course work involving policy and network security.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('INF', '529', 'Security and Privacy in Informatics', 'Clifford Neuman', 1184968, 'Covers societal implications of information privacy and how to design systems to best preserve privacy. Recommended preparation: General familiarity with the use of common internet and mobile applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '549', 'Introduction to Computational Thinking and Data Science', 'Deborah Khider', 'Introduction to data analysis techniques and associated computing concepts for non-programmers. Topics include foundations for data analysis, visualization, parallel processing, metadata, provenance, and data stewardship. Recommended preparation: Mathematics and logic undergraduate courses.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '550', 'Overview of Data Informatics in Large Data Environments', 'Christian Mattmann', 'Fundamentals of big data informatics techniques. Data lifecycle; the data scientist; machine learning; data mining; NoSQL databases; tools for storage/processing/analytics of large data set on clusters; in-data techniques. Recommended preparation: A basic understanding of engineering principles and programming language is desirable.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('INF', '551', 'Foundations of Data Management', 'Wensheng Wu', 2332803, 'Function and design of modern storage systems, including cloud; data management techniques; data modeling; network attached storage, clusters and data centers; relational databases; the map-reduce paradigm.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '552', 'Machine Learning for Data Informatics', 'Mohammad Reza Rajati', 'Practical applications of machine learning techniques to real-world problems. Uses in data mining and recommendation systems and for building adaptive user interfaces.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('INF', '553', 'Foundations and Applications of Data Mining', 'Yao-Yi Chiang', 2391740, 'Data mining and machine learning algorithms for analyzing very large data sets. Emphasis on Map Reduce. Case studies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '555', 'User Interface Design, Implementation, and Testing', 'Jaime Levy', 'Understand and apply user interface theory and techniques to design, build and test responsive applications that run on mobile devices and/or desktops.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INF', '556', 'User Experience Design and Strategy', 'Jaime Levy', 'The practice of User Experience Design and Strategy principles for the creation of unique and compelling digital products and services.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('INF', '558', 'Building Knowledge Graphs', 'Wensheng Wu', 2332803, 'Foundations, techniques, and algorithms for building knowledge graphs and doing so at scale. Topics include information extraction, data alignment, entity linking, and the Semantic Web.', 4);
