INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('INF', '560', 'Data Informatics Professional Practicum', 'Anna Farzindar', 2357715, 'Student teams working on external customer data analytic challenges; project/presentation based; real client data, and implementable solutions for delivery to actual stakeholders; capstone to degree.', 4);
