INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '210', 'Drawing II', 'Ruth Weisberg', 'Drawing methods and concepts, with emphasis on observing and rendering the human form and anatomy. Continued acquisition of skills, with compositional, gestural, and interpretive elements.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '220', 'Painting II', 'Keith Mayerson', 'Continuation of practical and theoretical skills introduced in Painting I. Color in personal expression, perception, and content; cultural subjects/objects and symbols signifying a personal language.', 4);
