INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '242', 'Metal', '', 'Introduction to metal in fine art. Emphasis on technical proficiencies and effective solutions working in a variety of metals.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ART', '250', 'Photography II', 'Julia Paull', 739310, 'Continued examination of the conceptual, technical, aesthetic and historical tools necessary for a well-informed deliberate art practice with an emphasis on color photography. Work is in digital or analog.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '260', 'Video and Time-based Media', 'Patty Chang', 'An introductory course exploring contemporary processes and practices of video experimentation including the camera, desktop production, and editing. Experimentation with multiple modes of execution, presentation, and distribution.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '280', 'Studio Critique I', 'Jennifer West', 'Interdisciplinary forum where students develop research skills, learn how to best articulate their artistic production, discuss materials related to their research interests, and critique the work of their peers.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ART', '310', 'Advanced Drawing', 'Edgar Arceneaux', 2308647, 'Directed examination of personal approaches in drawing; emphasis on making visual and artistic decisions and developing an appropriate attitude towards individual expression.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '311', 'Anatomical Drawing from Life', '', 'An advanced, concentrated study of the human figure; expands on skills depicting and visualizing the human form.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '312', 'Comics Project', 'Keith Mayerson', 'All aspects of comics will be covered, from writing and composition to inking and marketing through the production of an individual 15-page comic creation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '315', 'Studies in Drawing and Painting', 'Ruth Weisberg', 'A studio examination of rotating topics in drawing/painting, either media-based or content-driven, with individual and collaborative projects; topics change each semester.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ART', '320', 'Advanced Painting', 'Karen Liebowitz', 1793319, 'Directed examination of aesthetic concepts; investigation into personal ideas related to the development of a creative visual language in painting.', 4);
