INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '331', 'Clay and Glazes', '', 'A foundation in the formulation of clay bodies and glaze materials through lectures and studio exercises to suit specific needs with the artist in mind.', 2);
