INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '350', 'Advanced Photography', 'David Kelley', 'Continued examination of the conceptual, technical, aesthetic and historical tools necessary for a well-informed deliberate art practice with an emphasis on color photography. Work is in digital or analog.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '362', 'Ideas in Intermedia', 'Jennifer West', 'An examination of the impact of digital media on contemporary culture, with attention to a particular, changing topic each semester.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '370', 'Printmaking', '', 'Introductory course in various printmaking techniques; necessary skills and inherent expressive qualities of different printmaking methods are explored.', 4);
