INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '430', 'Topics in Advanced Ceramics', '', 'Continued directed examination of aesthetic concepts and investigation into personal ideas as they relate to the development of a creative visual language in ceramics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ART', '440', 'Topics in Advanced Sculpture', 'Jud Fine', 1361622, 'Individual direction of the study of aesthetic issues in sculpture; investigation of an individual creative vision.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '470', 'Topics in Advanced Printmaking', '', 'Directed examination of specific printmaking media in relation to personal aesthetic goals and expressive concepts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ART', '485', 'Studio Capstone', 'Marisa Mandler', 2051571, 'A faculty-mentored course supporting advanced conceptual exploration and the development of an ambitious, self-defined series or body of work, culminating in an exhibition.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '499', 'Special Topics', 'Thomas Mueller,Karen Liebowitz', 'Comprehensive exploration of particular aspects of visual art.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '515', 'Visiting Artist and Scholar Seminar', 'Karen Moss', 'Lecture and discussion course in the professional practice of art featuring formal presentations by visiting artists and scholars. (Duplicates credit in FA 515)', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '535', 'Group Critique', 'Mary Kelly', 'Presentation and critique which builds students\' skills in presenting and contextualizing their artwork for peer review; analytic discussion regarding directions in contemporary art.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ART', '599', 'Special Topics', 'Suzanne Lacy', 'Comprehensive exploration of particular aspects of the history of art. (Duplicates credit in former FA 599)', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CRIT', '150gp', 'Histories of Art, Design and Visual Culture', 'Andrew Campbell', 2168101, 'A loosely chronological, thematic survey of art, visual culture, design, and critical theory from the early modern period through modernism (1500 1950).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CRIT', '160g', 'Critical Theory in Art, Design and Visual Culture', '', 'An issues-based introduction to critical theory in the twentieth and twenty-first centuries, especially as it relates to philosophy, art, design, and visual culture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CRIT', '350gw', 'Global Art, Design and Visual Culture since 1960', '', 'A critical history of art, design, visual culture, and visual theory since 1960, addressing through a postcolonial/decolonial lens the concepts and practices of "global" art.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CRIT', '415', 'Performance Art since 1960', '', 'The role of the body, performance, action, and the question of the event in contemporary art and design.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CRIT', '425', 'Identity and Visual Representation', 'Amelia Jones', 2116434, 'A critical study of the relationship between identity or coalitional politics and specific practices, meanings, and sitings of contemporary art and design.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CRIT', '525', 'Pedagogy and Praxis', 'Andrew Campbell', 2168101, 'Provides students with a foundation in the history and theory of studio art and curatorial education and develops their teaching skills in these areas.', 4);
