INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CRIT', '557', 'Group Project Summation', 'Karen Moss', 'Third in a sequence of three courses on history/theory of curatorial methods, with practical experience necessary to mounting exhibitions and devising interfaces with the public. Prerequisite: CRIT 556. (Duplicates credit in former PAS 555c)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '102', 'Design Fundamentals', '', 'Introduction to the basic elements and processes of visual communication and design. Instruction includes studio projects, lectures and readings. Various media used.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '123xg', 'Exploring the Design Process', '', 'An overview of the creative design process and introduction to techniques to solve visual problems.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '202', 'Design II', 'Osvaldo Trujillo', 'Exploration of essential elements of traditional and digital design, including color and image-text integration, editing information, typography and sequencing.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '203', 'Digital Tools for Design', '', 'Exploration of the processes of germination, ideation and application/execution in design integrated with computer usage.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '213', 'Digital Tools in 3D Design', 'Jeffrey Cain', '3D digital modeling, rendering, digital capture and production methods for product, package and environmental design.', 2);
