INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '302', 'Design III', '', 'Advanced exploration of typography and image-text integration, including collaborative projects, pre-press and proofing techniques, narrative concepts and information architecture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '303', 'Web Design', '', 'A workshop-based course that focuses on software applications in design and web design.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '313', 'Graphic Design in Fashion', '', 'An intensive studio and lecture course focusing on the applications of graphic design in fashion and related fields.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DES', '322', 'Publication Design', 'Jason Ellenburg', 2304999, 'Application of layout, typography, design principles and image making in the design and production of books, magazines, zines and digital books.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '323g', 'Design Theory', '', 'A comprehensive study of visual communication focusing on graphic design from 1900 to the present. An introduction to design thinking, language and principles including political and cultural implications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '332a', 'Typography', '', 'The study of visual communication through the use of letterforms from historical tradition to contemporary experimental rebellion.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DES', '332b', 'Typography', 'Haven Lin-Kirk', 1046084, 'Continuation of a.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '333', 'New York Design Study Tour', 'Brian O\'Connell,Alexis Zoto', 'Historical and contemporary aspects of the New York design world, including a one-week trip to New York City to visit design firms, studios and museums. Airfare and accommodations extra. By application only.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DES', '402', 'Advanced Design Projects', 'Jason Ellenburg', 2304999, 'Advanced information design within a flexible curriculum. Emphasis on team-oriented projects.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DES', '413', 'Professional Practice in Design', 'Ewa Wojciak', 719151, 'A summation of one\'s practice, with a focus on transitioning to a career as a professional designer.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('DES', '432', 'Special Projects in Design', 'Ewa Wojciak', 719151, 'Students work on projects for outside clients, and receive instruction in professional practices, advanced design techniques, and sophisticated technology. By invitation or portfolio review only. Only open to upper division students.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '494a', 'Senior Thesis', '', 'Research and writing of a senior-level thesis or thesis project under the supervision of a faculty committee.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '514', 'Designers in Residence Forum', '', 'Seminar in the professional practice of design featuring formal presentations and mentoring opportunities by designers in residence.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '523', 'Design Theory', '', 'A critical examination of issues, theories, movements and practices that are relevant to the contemporary professional designer.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('DES', '599', 'Special Topics', '', 'Selected topics in the various specialty areas within design at the graduate level. Topic will vary from semester to semester.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '121gp', 'Renaissance to Modern', 'Amy Ogata', 2025331, 'European art and its legacy in the Americas. Painting, sculpture, architecture and other visual media considered in relation to social and cultural history.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '201g', 'Digging into the Past', 'John Pollini', 947757, 'A broad survey, covering some 8,000 years and focusing on the material culture of the ancient world in a historical and social context.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AHIS', '220g', 'Medieval Visual Culture', 'Carolyn Malone', 947756, 'Medieval visual culture as an introduction to the Christian heritage of western civilization and to the interaction of Church and state from the 3rd to the 13th century.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '301gp', 'America, the Frontier, and the New West', 'Thomas Gustafson', 315213, 'Introduction to an interdisciplinary study of American political, cultural and social life with a particular emphasis on the Western United States as a region. Recommended preparation: HIST 200, ENGL 263', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '151gp', 'The Legacy of Rome', 'Anthony Boyle', 1210125, 'Ancient Roman values, ideas, and institutions of relevance to later periods of civilization.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '280gp', 'Classical Mythology in Art and Literature', 'Thomas Habinek', 34529, 'Classical Greek and Roman mythology as inspiration for literature, art, music, and film in various historical contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '251g', 'Modern Literature and Thought of the West Since 1800', 'Roberto Diaz', 1168906, 'Survey of literary and other cultural texts from the 19th to the 21st centuries, with emphasis on the individual and social change.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '374mg', 'Women Writers in Europe and America', 'Margaret Rosenthal', 1207521, 'Introduction to works of major women writers from the Middle Agesto the 20th century in their literary, social, and cultural contexts.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HIST', '102mg', 'Medieval People', 'Jason Glenn', 136404, 'Early Europe, c 400-1500, with a focus on Europe\'s diverse communities, cultural interactions among them, dealings between conquerors and conquered, and european contacts with non-Europeans.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HIST', '103g', 'The Emergence of Modern Europe', 'Lindsay O\'Neill', 1642149, 'Political, intellectual, and cultural developments in Europe,1300-1815. Renaissance and Reformation; absolute monarchy,scientific changes, and Enlightenment; French Revolution andNapoleon.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('JS', '100gp', 'Jewish History', 'Joshua Garroway,Leah Hochman', 'Major ideas, personalities, and movements in Jewish history from antiquity to the present in light of the interaction of the Jews with the general culture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('JS', '314gp', 'Jews, Christians, Muslims', 'Reuven Firestone,Leah Hochman', 'Investigates the engagement in war by Judaism, Christianity and Islam by examining history and theology and looks at religious justifications and condemnations of war.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PHIL', '110g', 'Modern Foundations of Western Thought', 'Adriana Solomon', 'Explores the writings of philosophers, from Descartes to Kant, who responded to, and helped to shape, the scientific and political upheavals of the modern period.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('REL', '121g', 'The World of the New Testament', 'Cavan Concannon', 2020276, 'Historical investigation of New Testament characters, events, ethics and theology in relation to its social, intellectual, and religious contexts in the Jewish and Greco-Roman world.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('REL', '125gp', 'Introduction to Christianity', 'David Albertson', 1192279, 'Survey of the changing beliefs and practices of the Christian religion from obscure origins to globalized present, with special attention to the varieties of Christian literature.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('AHIS', '128gp', 'Arts of Latin America', '', 'Survey of the art, architecture, and visual culture of Latin America from the colonial period to the present, focusing on connections to culture and society.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '135mgw', 'Peoples and Cultures of the Americas', 'Alicia Chavez', 1859228, 'An introduction to cultures and people in the Americas; the social, historical, economic and cultural formations that together make up the Latino/a American imaginary.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('AMST', '250mgw', 'The African Diaspora', 'Edwin Hill', 2067531, 'History, political-economy and aesthetics of the African Diaspora with emphasis on Latin America, the Caribbean, Europe and Africa.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ANTH', '263g', 'Exploring Culture Through Film', 'Xochitl Ruiz', 'Concepts of social anthropology using filmic representations ofsocieties throughout the world in contrast to written ethnography.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ANTH', '315g', 'North American Indians', 'Tok Thompson', 1207495, 'North American Indian societies, their major cultural themes,ethnological significance, and comparability with Western Europeancultural forms; lectures, visual, and indigene demonstrations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CLAS', '349g', 'Ancient Empires', 'Christelle Fischer-Bovet', 1779824, 'History and cultures of the ancient empires of southwest Asia, from Cyrus the Great to the establishment of Islam.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COLT', '102g', 'The Place of Literature in Global Cultures', 'Michael Du Plessis', 1307922, 'Comparative study of works from a broad range of cultural traditions that originate from, and provide insight into, vital global locations outside the Western sphere.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '110gp', 'The Great Tradition', 'Geraldine Fiss', 2088599, 'Introduction to the major humanities traditions of China, Japan, and Korea through an examination of representative works drawnfrom literature, aesthetics, philosophy, religion, and historicalwriting.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('EALC', '125g', 'Introduction to Contemporary East Asian Cinema and Culture', 'Kerim Yasar', 'An introduction to and overview of the contemporary cinemas of East Asia: China (Hong Kong, the People\'s Republic, and Taiwan), Japan, and Korea.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EALC', '358g', 'Transnational Chinese Literature and Culture', 'Brian Bernards', 1670170, 'An introduction to Sinophone literatures and cultures (in English translation) from the Asia-Pacific region, including Taiwan, Hong Kong, Tibet, Southeast Asia, and North America.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('EASC', '160mgp', 'China and the World', 'Brian Bernards', 1670170, 'Advanced-level introduction to China and its relations with the wider world in historic and contemporary perspective.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HIST', '106g', 'An Introduction to Chinese History', 'Joshua Goldstein', 1384299, 'Study of the lives of selected individuals who have helped to shape Chinese politics and culture.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PORT', '250g', 'Cultures of Brazil and Lusophone Africa', 'Ellen Oliveira', 1754235, 'Comparative study of Brazil in the context of the Lusophone (Portuguese-speaking) world, especially Portugal\'s former colonies in Africa. Materials drawn from literature, visual culture, music and cultural theory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('REL', '134g', 'Introduction to Buddhism', 'Lori Meeks', 491417, 'Focus primarily on works of Buddhist literature written in a variety of genres. Introduction of basic teachings that link Buddhist traditions across time and space.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SLL', '330gp', 'Russian Thought and Civilization', 'Sally Pratt', 'Russian cultural identity from its beginnings until today. The Eastern Orthodox tradition, its traumatic confrontation with Western culture, and their continuous interaction.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '100Lxg', 'The Universe', 'Vahe Peroomian', 2002099, 'Survey of the universe: planets, satellites, comets, stars, nebulae, galaxies. Practical component includes planetary observations and dark-sky field trip. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '104Lxg', 'How the Body Works', 'Chien-Ping Ko,Shirin Birjandi', 'Structure and function of the human body, including the role of organ systems, tissues, and cells in normal function. Malfunctions resulting from disease, substance abuse and lifestyle. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '105Lg', 'Planet Earth', 'John Platt', 1114558, 'Geologic structure and evolution of planet earth. Principles ofplate tectonics, rocks and minerals, processes of mountainbuilding, continent and ocean formation, earthquakes, volcanism,development of landforms by running water and glaciers. Lecture, 3hours; laboratory, 2 hours. One all-day or two-day field triprequired.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '107Lg', 'Oceanography', 'Steve Lund', 1779351, 'Physical, chemical, and geological character of the oceans andocean basins. Origin of the oceans. Ocean processes and agents.Economic value of the oceans. Lecture, 3 hours; laboratory, 2 hours. One all-day field trip required.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '100Lxg', 'The Physical World', 'Gene Bickers', 143437, 'The fundamentals of physics presented with emphasis on the structure and beauty of physical laws. Practical component will relate these laws to commonly encountered events. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '161Lg', 'Advanced Principles of Physics I', 'Gene Bickers', 143437, 'Gateway to the majors and minors in Physics and Astronomy. Introductory treatment intended for well-qualified students. Dynamics of particles and rigid bodies, conservation laws, wave motion, thermodynamics, heat engines, entropy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '102Lxg', 'Humans and Their Environment', 'Dale Kiefer,James Moffett', 'An examination of the physical and biological laws that influence agriculture, pollution, population dynamics (including humans), climate, biodiversity and ecosystem structure and function. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '150Lxg', 'The Nature of Human Health and Disease', 'Raffaella Ghittoni,Nancy Castro', 'The human organism; the nature of inherited and acquired diseases;the biological and societal basis for the AIDS epidemic; therapy,drug design and the future. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('BISC', '180Lxg', 'Evolution', 'Oliver Rizk', 'Changes in the physical and biological universe over time; origins of life, dinosaurs, human evolution. Implications of evolutionary mechanisms and mass extinctions for human survival.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('BISC', '230Lxg', 'The Biology of the Brain', 'Bruce Yazejian', 1939387, 'The structure and function of the mammalian brain including the role of the brain in regulating behavior, both in normal and diseased states; in relation to mind; and in comparison with machine forms of intelligence.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '205Lxg', 'The Science, and Its Impact', 'Thomas Bertolini,Rebecca Broyer,Jasmine Bryant', 'Scientific principles underlying forensic approaches to the investigation of crimes and its societal impact on law, culture and media. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '241Lg', 'Energy Systems', 'Joshua West', 1582412, 'Energy resources from a global perspective, including fossil fuels, nuclear, and renewable energy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HBIO', '200Lg', 'The Human Animal', 'Trond Sigurdsen', 2235391, 'Foundations of the human species. Examination of scientific evidence from Darwinian theory, primate behavior, fossils, and the behavior of modern people. Laboratory. Duplicates credit in former ANTH 200Lg.', 4);
