INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GERO', '315g', 'A Journey into the Mind', 'John Walsh', 1285191, 'Introduction to the brain and mind. A unique multimedia approach to stress how knowledge about the mind is gained from scientific and clinical investigations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GESM', '140g', 'Seminar in the Life Sciences', 'Elsi Kaiser', 2154093, 'Scientific understanding of a full range of living systems from molecules to ecosystems, prokaryotes to humans, past and present. (Satisfies GE-D, Life Sciences) Open only to Freshmen.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GESM', '141g', 'Seminar in the Life Sciences', 'Aaron Hagedorn', 2009416, 'Scientific understanding of living systems from molecules to ecosystems; how data is generated and interpreted; how scientific discovery spurs technological growth and impacts society. Offered by a professional school.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HBIO', '200Lg', 'The Human Animal', 'Trond Sigurdsen', 2235391, 'Foundations of the human species. Examination of scientific evidence from Darwinian theory, primate behavior, fossils, and the behavior of modern people. Laboratory. Duplicates credit in former ANTH 200Lg.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HBIO', '202g', 'Nutrition for Life', 'Bob Girandola', 1129012, 'To develop an understanding of Nutrition, especially as it pertains to optimal health and prevention of diseases. A study of macro and micro nutrients.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HBIO', '205Lxg', 'The Science of Sport', 'Bob Girandola', 1129012, 'The physiological and nutritional basis of human performance. Factors that facilitate and limit athletic achievement. Lecture, 3 hours; laboratory, 2 hours. Not available for major credit. Duplicates credit in former EXSC 205Lxg.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('LING', '110g', 'In a Word', 'Hajime Hoji', 1710843, 'Words as a gateway to the human mind. How words are stored, comprehended and retrieved. How words are constructed. Words and concepts. Words and social constructs. The processing and the acquisition of words in normal and atypical children and adults.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PHIL', '246Lg', 'Foundations of Cognitive Science', 'Alexis Wellwood', 'Introduction to research in interdisciplinary cognitive science, highlighting ideas and methods from philosophy, psychology, linguistics, neuroscience, and computer science.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PSYC', '100Lg', 'Introduction to Psychology', 'Clayton Stephenson', 1582754, 'Introduction to psychological science, including historical and contemporary approaches. Behavior examined from biological, cognitive, social, developmental, and personality perspectives. Disorders and treatments.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PSYC', '165Lg', 'Drugs, Behavior, and Society', 'John Monterosso', 1167371, 'An integrative systems perspective of drugs; including their historical, economic, and cultural importance, psychopharmacology, addiction, relationship to crime, and therapeutic use in treating psychological disorders.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '100Lxg', 'The Universe', 'Vahe Peroomian', 2002099, 'Survey of the universe: planets, satellites, comets, stars, nebulae, galaxies. Practical component includes planetary observations and dark-sky field trip. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('ASTR', '200Lg', 'Life in the Universe', 'Vahe Peroomian', 2002099, 'Survey of the prospects for life in our solar system and beyond, based on the evolution of life on Earth.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('CHEM', '105aLg', 'General Chemistry', 'Thomas Bertolini', 1436158, 'Fundamental principles and laws of chemistry; laboratory work emphasizes quantitative procedures. Prerequisite to all more advanced courses in chemistry. Lecture, 3 hours; laboratory and discussion, 4 hours. Quiz, 1 hour.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('CHEM', '205Lxg', 'The Science, and Its Impact', 'Thomas Bertolini,Rebecca Broyer,Jasmine Bryant', 'Scientific principles underlying forensic approaches to the investigation of crimes and its societal impact on law, culture and media. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '105Lg', 'Planet Earth', 'John Platt', 1114558, 'Geologic structure and evolution of planet earth. Principles ofplate tectonics, rocks and minerals, processes of mountainbuilding, continent and ocean formation, earthquakes, volcanism,development of landforms by running water and glaciers. Lecture, 3hours; laboratory, 2 hours. One all-day or two-day field triprequired.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '107Lg', 'Oceanography', 'Steve Lund', 1779351, 'Physical, chemical, and geological character of the oceans andocean basins. Origin of the oceans. Ocean processes and agents.Economic value of the oceans. Lecture, 3 hours; laboratory, 2 hours. One all-day field trip required.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('GEOL', '241Lg', 'Energy Systems', 'Joshua West', 1582412, 'Energy resources from a global perspective, including fossil fuels, nuclear, and renewable energy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('LING', '285Lg', 'Human Language and Technology', 'Mary Byram Washburn', 'Study of human linguistic competence and technologies that simulate it. Grammar, parsing, text generation; semantics, pragmatics, sense disambiguation; phonetics, speech synthesis, speech recognition.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '100Lxg', 'The Physical World', 'Gene Bickers', 143437, 'The fundamentals of physics presented with emphasis on the structure and beauty of physical laws. Practical component will relate these laws to commonly encountered events. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PHYS', '125Lg', 'Physics for Architects', 'Christopher Sutherland', 'Fundamental laws and principles of physics with emphasis on theapplication of physical principles to the problems ofarchitecture. Lecture, 4 hours; laboratory, 3 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '135aLg', 'Physics for the Life Sciences', 'Moh El-Naggar', 1423549, 'Fundamental laws and principles of physics emphasizing areasrelated to life sciences. Prerequisite for Biological Sciences,Medicine, Dentistry, Pharmacy. Lecture, 4 hrs.; Lab, 3 hrs.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PHYS', '151Lg', 'Mechanics and Thermodynamics', 'Christopher Sutherland', 'Gateway to the majors and minors in Physics and Astronomy. Statics and dynamics of particles and rigid bodies, conservation principles, gravitation, simple harmonic oscillators, thermodynamics, heat engines, entropy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '161Lg', 'Advanced Principles of Physics I', 'Gene Bickers', 143437, 'Gateway to the majors and minors in Physics and Astronomy. Introductory treatment intended for well-qualified students. Dynamics of particles and rigid bodies, conservation laws, wave motion, thermodynamics, heat engines, entropy.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '171Lg', 'Mechanics', 'Douglas Burke', 1564049, 'Mechanics, mechanical vibrations and waves, special relativity. Emphasizes applications in the areas of applied physics and engineering.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PHYS', '200Lxg', 'The Physics and Technology of Energy', 'Hans Bozler', 1898551, 'Investigation of energy technologies, including development and implementation issues. Topics include the Industrial Revolution, electromagnetic induction, power transmission, combustion engines, fission and fusion. Not available for major credit.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SSCI', '265Lg', 'The Water Planet', 'John Wilson,Elisabeth Sedano', 'An exploration of earth\'s water, ranging from water properties, chemistry, and pollution, to groundwater dynamics, watershed processes, and oceanic-atmospheric circulation. Implications for past and future societies. Lecture and laboratory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('ARCH', '213bg', 'Building Structures and Seismic Design', 'G G Schierle', 'Investigation and design of structure systems : their resistance toseismic and wind forces and integration with architectural design for synergy of form and structure.', 3);
