INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('COMM', '301Lg', 'Empirical Research in Communication', 'Carmen Lee', 2077605, 'Experimental and survey methods for communication study; basic statistical concepts, procedures, and tests.', 4);
