INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('LAW', '101w', 'Law and the U.S. Constitution in Global History', 'Samuel Erman', 2257849, 'By examining key constitutional moments involving race, rights, and revolutions, students will explore how legal meaning changes over time.', 4);
