INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('LAW', '403', 'Mental Health Law', 'Elizabeth Carroll', 'Issues at the intersection of law and psychology, both civil--e.g. civil commitment--and criminal-e.g. the insanity defense. Emphasis on ethical issues.', 4);
