INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('LAW', '502', 'Procedure I', '', 'Consideration of the participants in litigation; private and public plaintiffs, defendants, and courts. Information exchange, process, outcomes, and costs of lawsuits.', 2);
